SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_gerar_interf_i350_fcont ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE



cd_classificacao_w		varchar(40);
cd_conta_contabil_w		varchar(40);
cd_centro_custo_w		integer;
dt_resultado_w			timestamp;
dt_referencia_w			timestamp;
ie_apres_conta_ctb_w		varchar(15);
nr_seq_mes_ref_w		bigint;
vl_saldo_w			varchar(80);
ie_debito_credito_w		varchar(2);
nr_linha_w			bigint	:= qt_linha_p;
nr_seq_registro_w		bigint	:= nr_sequencia_p;
ds_arquivo_w			varchar(4000);
ds_compl_arquivo_w		varchar(4000);
ds_linha_w			varchar(8000);
sep_w				varchar(1)	:= '|';
tp_registro_w			varchar(15);
ie_periodo_w			varchar(1);
ie_consolida_empresa_w		ctb_regra_sped.ie_consolida_empresa%type;

c01 CURSOR FOR
	SELECT	nr_sequencia,
		fim_mes(dt_referencia)
	from   	ctb_mes_ref
	where	ie_periodo_w	= 'A'
	and	cd_empresa	= cd_empresa_p
	and	dt_referencia	= dt_referencia_w
	
union all

	SELECT	nr_sequencia,
		fim_mes(b.dt_referencia) dt_referencia
	from	ctb_mes_ref b
	where	b.dt_referencia	between dt_inicio_p and dt_fim_p
	and	b.cd_empresa	= cd_empresa_p
	and	ie_periodo_w	= 'T'
	and	substr(to_char(b.dt_referencia,'mm'),1,2) in ('03','06','09','12')
	order by
		1;

c02 CURSOR FOR
	SELECT	a.cd_conta_contabil,
		a.cd_classificacao,
		(coalesce(sum(a.vl_saldo),0) - coalesce(sum(vl_encerramento),0)) vl_saldo,
		substr(ctb_obter_situacao_saldo(a.cd_conta_contabil,  coalesce(sum(a.vl_saldo),0) - coalesce(sum(vl_encerramento),0)),1,1) ie_debito_credito
	from	ctb_grupo_conta d,
		conta_contabil c,
		ctb_saldo a
	where	c.cd_conta_contabil	= a.cd_conta_contabil
	and	d.cd_grupo		= c.cd_grupo
	and	c.ie_tipo		= 'A'
	and	d.ie_tipo in ('R','C','D')
	and	a.nr_seq_mes_ref	= nr_seq_mes_ref_w
	and	a.cd_estabelecimento	= cd_estabelecimento_p
	and	ie_consolida_empresa_w	= 'N'
	group by
		a.cd_conta_contabil,
		a.cd_classificacao
	
union

	SELECT	a.cd_conta_contabil,
		a.cd_classificacao,
		(coalesce(sum(a.vl_saldo),0) - coalesce(sum(vl_encerramento),0)) vl_saldo,
		substr(ctb_obter_situacao_saldo(a.cd_conta_contabil,  coalesce(sum(a.vl_saldo),0) - coalesce(sum(vl_encerramento),0)),1,1) ie_debito_credito
	from	ctb_grupo_conta d,
		conta_contabil c,
		ctb_saldo a,
		estabelecimento e
	where	c.cd_conta_contabil	= a.cd_conta_contabil
	and	a.cd_estabelecimento	= e.cd_estabelecimento
	and	coalesce(ie_gerar_sped,'S')	=	'S'
	and	d.cd_grupo		= c.cd_grupo
	and	c.ie_tipo		= 'A'
	and	d.ie_tipo in ('R','C','D')
	and	a.nr_seq_mes_ref	= nr_seq_mes_ref_w
	and	ie_consolida_empresa_w	= 'S'
	group by
		a.cd_conta_contabil,
		a.cd_classificacao
	order by
		cd_classificacao;



BEGIN
dt_referencia_w	:= trunc(dt_fim_p,'mm');

select	coalesce(max(a.ie_apres_conta_ctb),'CD'),
	coalesce(max(ie_periodo),'A'),
	coalesce(max(a.ie_consolida_empresa), 'N')
into STRICT	ie_apres_conta_ctb_w,
	ie_periodo_w,
	ie_consolida_empresa_w
from	ctb_regra_sped		a,
	ctb_sped_controle	b
where	b.nr_seq_regra_sped 	= a.nr_sequencia
and	b.nr_sequencia		= nr_seq_controle_p;

open C01;
loop
fetch C01 into
	nr_seq_mes_ref_w,
	dt_resultado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	tp_registro_w 		:= 'I350';
	--dt_resultado_w		:= trunc(fim_mes(dt_resultado_w));
	ds_linha_w		:= substr(	sep_w || tp_registro_w 				||
						sep_w || to_char(dt_resultado_w,'ddmmyyyy')	|| sep_w,1,8000);

	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_compl_arquivo_w	:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w	:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;

	insert into ctb_sped_registro(nr_sequencia,
		ds_arquivo,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_controle_sped,
		ds_arquivo_compl,
		cd_registro,
		nr_linha)
	values (nr_seq_registro_w,
		ds_arquivo_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_controle_p,
		ds_compl_arquivo_w,
		tp_registro_w,
		nr_linha_w);

	tp_registro_w	:= 'I355';

	open C02;
	loop
	fetch C02 into
		cd_conta_contabil_w,
		cd_classificacao_w,
		vl_saldo_w,
		ie_debito_credito_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		if (ie_apres_conta_ctb_w = 'CL') then
			cd_conta_contabil_w	:= cd_classificacao_w;
		elsif (ie_apres_conta_ctb_w = 'CP') then
			cd_conta_contabil_w	:= substr(replace(cd_classificacao_w,'.',''),1,40);
		end if;

		if (vl_saldo_w <> 0) then
			ds_linha_w	:= substr(	sep_w || tp_registro_w				||
							sep_w || cd_conta_contabil_w 			||
							sep_w || ''					||
							sep_w || sped_obter_campo_valor(vl_saldo_w) 	||
							sep_w || ie_debito_credito_w			|| sep_w,1,8000);

			ds_arquivo_w		:= substr(ds_linha_w,1,4000);
			ds_compl_arquivo_w	:= substr(ds_linha_w,4001,4000);
			nr_seq_registro_w	:= nr_seq_registro_w + 1;
			nr_linha_w		:= nr_linha_w + 1;

			insert into ctb_sped_registro(nr_sequencia,
				ds_arquivo,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_controle_sped,
				ds_arquivo_compl,
				cd_registro,
				nr_linha)
			values (nr_seq_registro_w,
				ds_arquivo_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_controle_p,
				ds_compl_arquivo_w,
				tp_registro_w,
				nr_linha_w);
		end if;
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ctb_gerar_interf_i350_fcont ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) FROM PUBLIC;

