CREATE OR REPLACE PROCEDURE ctb_gerar_interf_j005_ecd ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE



dt_inicial_w			timestamp;
dt_final_w			timestamp;			
ds_arquivo_w			varchar(4000);
ds_compl_arquivo_w		varchar(4000);
ds_linha_w			varchar(8000);
nr_linha_w			bigint := qt_linha_p;
nr_seq_registro_w		bigint := nr_sequencia_p;
tp_registro_w			varchar(15);
sep_w				varchar(1) := '|';
ie_demonstrativo_w		ctb_regra_sped.ie_demonstrativo%type;
ds_demonstrativo_w		ctb_regra_sped.ds_demonstrativo%type;


BEGIN

select	coalesce(max(a.ie_demonstrativo), '1'),
        coalesce(max(a.ds_demonstrativo), '')
into STRICT	ie_demonstrativo_w,
	ds_demonstrativo_w
from	ctb_regra_sped		a,
	ctb_sped_controle	b
where	b.nr_sequencia = nr_seq_controle_p
and 	a.nr_sequencia	= b.nr_seq_regra_sped; 	

dt_inicial_w 	:= dt_inicio_p;
dt_final_w 	:= dt_fim_p;

tp_registro_w 	:=	'J005';

ds_linha_w	:= substr(	sep_w || tp_registro_w 				||
				sep_w || to_char(dt_inicial_w,'ddmmyyyy')	|| 
				sep_w || to_char(dt_final_w,'ddmmyyyy') 	|| 
				sep_w || ie_demonstrativo_w			|| 
				sep_w || ds_demonstrativo_w			|| 
				sep_w,1,8000);

ds_arquivo_w		:= substr(ds_linha_w,1,4000);
ds_compl_arquivo_w	:= substr(ds_linha_w,4001,4000);
nr_seq_registro_w	:= nr_seq_registro_w + 1;
nr_linha_w		:= nr_linha_w + 1;	
	
	
insert into ctb_sped_registro(
		nr_sequencia,
		ds_arquivo,                     
		dt_atualizacao,                 
		nm_usuario,                     
		dt_atualizacao_nrec,            
		nm_usuario_nrec,                
		nr_seq_controle_sped,           
		ds_arquivo_compl,               
		cd_registro,
		nr_linha)
	values (	nr_seq_registro_w,
		ds_arquivo_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_controle_p,
		ds_compl_arquivo_w,
		tp_registro_w,
		nr_linha_w);
commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
