SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_gerar_interf_j150_ecd ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


ds_identificador_w				varchar(5);
cd_aglutinacao_w				varchar(40);
ie_nivel_aglutinacao_w				integer;
ds_aglutinacao_w				varchar(255);
nr_seq_demo_dre_w				bigint;
vl_conta_w					double precision;
ie_valor_w					varchar(2);
cd_instit_resp_cadastro_w			varchar(4);
cd_instituicao_w				varchar(21);
ds_arquivo_w					varchar(4000);
ds_compl_arquivo_w				varchar(4000);
ds_linha_w					varchar(8000);
nr_linha_w					bigint := qt_linha_p;
nr_seq_registro_w				bigint := nr_sequencia_p;
sep_w						varchar(1) := '|';
tp_registro_w					varchar(15) := 'J150';
cd_classificacao_w				varchar(40);
ie_apres_conta_ctb_w				varchar(15);
ie_saldo_w					varchar(1);
ie_consolida_empresa_w				ctb_regra_sped.ie_consolida_empresa%type;
dt_referencia_w					timestamp;
cd_versao_w					ctb_regra_sped.CD_VERSAO%type;
ind_grp_bal_w         varchar(1) := 'C';
ind_grp_bal_ant_w         			varchar(1) := 'C';

c01 CURSOR(
	dt_fim_pc  timestamp
	) FOR
SELECT	ds_identificador,
	cd_classificacao,
	cd_aglutinacao,
	ie_nivel_aglutinacao,
	ds_aglutinacao,
	vl_conta,
	substr(ctb_obter_situacao_result(a.cd_aglutinacao, null,vl_conta),1,1) ie_valor,
	a.dt_referencia dt_referencia
from	ecd_demonstracao_resultado_v a
where   a.dt_referencia 	= dt_fim_pc
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	ie_consolida_empresa_w	= 'N'

union

SELECT	ds_identificador,
	cd_classificacao,
	cd_aglutinacao,
	ie_nivel_aglutinacao,
	ds_aglutinacao,
	coalesce(sum(vl_conta),0) vl_conta,
	substr(ctb_obter_situacao_result(a.cd_aglutinacao, null,coalesce(sum(vl_conta),0)),1,1) ie_valor,
	a.dt_referencia dt_referencia
from	estabelecimento b,
	ecd_demonstracao_resultado_v a
where	a.cd_estabelecimento	= b.cd_estabelecimento
and	a.dt_referencia 	= dt_fim_pc
and	a.cd_empresa		= cd_empresa_p
and	ie_consolida_empresa_w	= 'S'
group by ds_identificador,
	cd_classificacao,
	cd_aglutinacao,
	ie_nivel_aglutinacao,
	ds_aglutinacao,
	a.dt_referencia
order by dt_referencia, cd_classificacao;

c02 CURSOR FOR
SELECT 	row_number() OVER () AS nr_ordem,
	cd_aglutinacao,
	nr_nivel,
	ds_aglutinacao,
	vl_conta,
	ie_cod_agl,
	cd_aglutinacao_sup,
	ind_grp_dre,
	ie_saldo,
	vl_conta_ant,
	ie_saldo_ant
from (	SELECT	a.nr_seq_rubrica	cd_aglutinacao,
		a.qt_desl_esq		nr_nivel,
		a.ds_rubrica ds_aglutinacao,
		a.vl_1_coluna vl_conta,
		CASE WHEN coalesce(b.ie_total,'N')='N' THEN 'D'  ELSE 'T' END  ie_cod_agl,
		b.nr_seq_rubrica_sup cd_aglutinacao_sup,
		b.ie_tipo_linha_sped ind_grp_dre,
		substr(ctb_obter_situacao_saldo_ecd(a.nr_seq_rubrica, 'J150',dt_fim_p, a.vl_1_coluna),1,1) ie_saldo,
		coalesce(a.vl_2_coluna, 0) vl_conta_ant,
		substr(ctb_obter_situacao_saldo_ecd(a.nr_seq_rubrica, 'J150',dt_fim_p, a.vl_2_coluna),1,1) ie_saldo_ant
	from	ctb_modelo_rubrica b,
		ctb_demo_rubrica_v a
	where	a.nr_seq_rubrica	= b.nr_sequencia
	and	a.nr_seq_demo		= nr_seq_demo_dre_w
	and	b.ie_situacao = 'A'
	and ((coalesce(a.vl_1_coluna,0) <> 0) or (coalesce(a.vl_2_coluna,0) <> 0))
	order by a.nr_seq_apres) alias11;

vet02	C02%RowType;


BEGIN

select	coalesce(max(nr_seq_demo_dre),0)
into STRICT	nr_seq_demo_dre_w
from	ctb_sped_controle
where	nr_sequencia	= nr_seq_controle_p;

select	coalesce(max(a.ie_apres_conta_ctb),'CD'),
	coalesce(max(ie_consolida_empresa), 'N'),
	coalesce(max(a.cd_versao), '9.0')
into STRICT	ie_apres_conta_ctb_w,
	ie_consolida_empresa_w,
	cd_versao_w
from	ctb_regra_sped a,
	ctb_sped_controle b
where	b.nr_seq_regra_sped 	= a.nr_sequencia
and	b.nr_sequencia		= nr_seq_controle_p;

if (nr_seq_demo_dre_w = -10) then --Inativo para a escritucao referente a 2018
	begin
	open C01(
		dt_fim_pc => trunc(dt_fim_p, 'mm')
		);
	loop
	fetch C01 into
		ds_identificador_w,
		cd_classificacao_w,
		cd_aglutinacao_w,
		ie_nivel_aglutinacao_w,
		ds_aglutinacao_w,
		vl_conta_w,
		ie_valor_w,
		dt_referencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		if (ie_apres_conta_ctb_w = 'CL') then
			cd_aglutinacao_w	:= cd_classificacao_w;
		elsif (ie_apres_conta_ctb_w = 'CP') then
			cd_aglutinacao_w	:= substr(replace(cd_classificacao_w,'.',''),1,40);
		end if;

		ds_linha_w	:= substr(	sep_w || tp_registro_w			||
					sep_w || cd_aglutinacao_w 			||
					sep_w || ie_nivel_aglutinacao_w 		||
					sep_w || ds_aglutinacao_w 			||
					sep_w || sped_obter_campo_valor(vl_conta_w) 	||
					sep_w || ie_valor_w 				|| sep_w,1,8000);

		ds_arquivo_w		:= substr(ds_linha_w,1,4000);
		ds_compl_arquivo_w	:= substr(ds_linha_w,4001,4000);
		nr_seq_registro_w		:= nr_seq_registro_w + 1;
		nr_linha_w		:= nr_linha_w + 1;

		insert into ctb_sped_registro(
				nr_sequencia,
				ds_arquivo,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_controle_sped,
				ds_arquivo_compl,
				cd_registro,
				nr_linha)
			values (	nr_seq_registro_w,
				ds_arquivo_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_controle_p,
				ds_compl_arquivo_w,
				tp_registro_w,
				nr_linha_w);
		end;
	end loop;
	close C01;
	end;
else
	begin
	open C02;
	loop
	fetch C02 into
		vet02;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		if (cd_versao_w in ('8.0','9.0')) then
			begin

      			if (vet02.vl_conta > 0 and vet02.ind_grp_dre = 'R') then
				ind_grp_bal_w := 'C';
			elsif (vet02.vl_conta < 0 and vet02.ind_grp_dre = 'R') then
				ind_grp_bal_w := 'D';
			elsif (vet02.vl_conta > 0 and vet02.ind_grp_dre = 'D') then
				ind_grp_bal_w := 'D';
			elsif (vet02.vl_conta < 0 and vet02.ind_grp_dre = 'D') then
				ind_grp_bal_w := 'C';
			end if;


			if (vet02.vl_conta_ant > 0 and vet02.ind_grp_dre = 'R') then
				ind_grp_bal_ant_w := 'C';
			elsif (vet02.vl_conta_ant < 0 and vet02.ind_grp_dre = 'R') then
				ind_grp_bal_ant_w := 'D';
			elsif (vet02.vl_conta_ant > 0 and vet02.ind_grp_dre = 'D') then
				ind_grp_bal_ant_w := 'D';
			elsif (vet02.vl_conta_ant < 0 and vet02.ind_grp_dre = 'D') then
				ind_grp_bal_ant_w := 'C';
			end if;

			ds_linha_w	:= substr(	sep_w || tp_registro_w					||
							sep_w || vet02.nr_ordem					||
							sep_w || vet02.cd_aglutinacao 				||
							sep_w || vet02.ie_cod_agl 				||
							sep_w || vet02.nr_nivel					||
							sep_w || vet02.cd_aglutinacao_sup			||
							sep_w || vet02.ds_aglutinacao				||
							sep_w || sped_obter_campo_valor(vet02.vl_conta_ant)	||
							sep_w || ind_grp_bal_ant_w				||
							sep_w || sped_obter_campo_valor(vet02.vl_conta)		||
							sep_w || ind_grp_bal_w					||
							sep_w || vet02.ind_grp_dre				||
							sep_w || ''						|| sep_w,1,8000);
			end;
		elsif (cd_versao_w = '7.0') then
			begin

      			if (vet02.vl_conta > 0 and vet02.ind_grp_dre = 'R') then
				ind_grp_bal_w := 'C';
			elsif (vet02.vl_conta < 0 and vet02.ind_grp_dre = 'R') then
				ind_grp_bal_w := 'D';
			elsif (vet02.vl_conta > 0 and vet02.ind_grp_dre = 'D') then
				ind_grp_bal_w := 'D';
			elsif (vet02.vl_conta < 0 and vet02.ind_grp_dre = 'D') then
				ind_grp_bal_w := 'C';
			end if;

			ds_linha_w	:= substr(	sep_w || tp_registro_w					||
							sep_w || vet02.cd_aglutinacao 				||
							sep_w || vet02.ie_cod_agl 				||
							sep_w || vet02.nr_nivel					||
							sep_w || vet02.cd_aglutinacao_sup				||
							sep_w || vet02.ds_aglutinacao				||
							sep_w || sped_obter_campo_valor(vet02.vl_conta)		||
							sep_w || ind_grp_bal_w					||
							sep_w || vet02.ind_grp_dre					||
							sep_w || ''						|| sep_w,1,8000);
			end;
		elsif (cd_versao_w = '6.0') then
			begin
			ds_linha_w	:= substr(	sep_w || tp_registro_w					||
							sep_w || vet02.cd_aglutinacao 				||
							sep_w || vet02.nr_nivel					||
							sep_w || vet02.ds_aglutinacao				||
							sep_w || sped_obter_campo_valor(vet02.vl_conta)		||
							sep_w || vet02.ie_saldo					||
							sep_w || sped_obter_campo_valor(vet02.vl_conta_ant)	||
							sep_w || vet02.ie_saldo_ant				||
							sep_w || ''						|| sep_w,1,8000);
			end;
		elsif (cd_versao_w in ('4.0','5.0')) then
			begin
			ds_linha_w	:= substr(	sep_w || tp_registro_w					||
							sep_w || vet02.cd_aglutinacao 				||
							sep_w || vet02.nr_nivel					||
							sep_w || vet02.ds_aglutinacao				||
							sep_w || sped_obter_campo_valor(vet02.vl_conta)		||
							sep_w || vet02.ie_saldo					||
							sep_w || sped_obter_campo_valor(vet02.vl_conta_ant)	||
							sep_w || vet02.ie_saldo_ant				|| sep_w,1,8000);
			end;
		else
			begin
			ds_linha_w	:= substr(	sep_w || tp_registro_w					||
							sep_w || vet02.cd_aglutinacao 				||
							sep_w || vet02.nr_nivel					||
							sep_w || vet02.ds_aglutinacao				||
							sep_w || sped_obter_campo_valor(vet02.vl_conta)		||
							sep_w || vet02.ie_saldo					|| sep_w,1,8000);
			end;
		end if;

		ds_arquivo_w		:= substr(ds_linha_w,1,4000);
		ds_compl_arquivo_w	:= substr(ds_linha_w,4001,4000);
		nr_seq_registro_w	:= nr_seq_registro_w + 1;
		nr_linha_w		:= nr_linha_w + 1;

		insert into ctb_sped_registro(
				nr_sequencia,
				ds_arquivo,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_controle_sped,
				ds_arquivo_compl,
				cd_registro,
				nr_linha)
			values (	nr_seq_registro_w,
				ds_arquivo_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_controle_p,
				ds_compl_arquivo_w,
				tp_registro_w,
				nr_linha_w);
		end;
	end loop;
	close c02;
	end;
end if;

commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE ctb_gerar_interf_j150_ecd ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) FROM PUBLIC;

