CREATE OR REPLACE PROCEDURE ctb_gerar_interf_j930_fcont ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE

 
			 
 
nr_crc_w		varchar(12);		
