ds_arquivo_w		varchar(4000);
ds_compl_arquivo_w	varchar(4000);
ds_linha_w		varchar(8000);
nr_linha_w		bigint := qt_linha_p;
nr_seq_registro_w	bigint := nr_sequencia_p;

c01 CURSOR FOR 
SELECT	'|' nr_crc, 
	obter_nome_pf(cd_titular) nm_signatario, 
	obter_dados_pf(cd_titular, 'CPF') cd_cpf, 
	'DIRETOR' ds_identificacao, 
	'203' cd_assinante_dnrc 
from	empresa 
where	cd_empresa	= cd_empresa_p 

union
 
SELECT	lpad(coalesce(nr_crc,0),11,0) || '|' nr_crc, 
	obter_nome_pf(cd_contabilista) nm_signatario, 
	obter_dados_pf(cd_contabilista, 'CPF') cd_cpf, 
	'CONTADOR' ds_identificacao, 
	'900' cd_assinante_dnrc 
from	empresa 
where	cd_empresa	= cd_empresa_p 
order by 5;


