	ds_identificacao_w, 
	cd_assinante_dnrc_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin	 
	ds_linha_w	:= substr('|' || 'J930' || '|' || nm_signatario_w || '|' || cd_cpf_w || '|' || ds_identificacao_w || '|' || cd_assinante_dnrc_w || '|' || 
					nr_crc_w,1,8000);
	 
	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_compl_arquivo_w	:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w	:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;	
	 
	insert into ctb_sped_registro( 
			nr_sequencia,          
			ds_arquivo,           
			dt_atualizacao,         
			nm_usuario,           
			dt_atualizacao_nrec,       
			nm_usuario_nrec,         
			nr_seq_controle_sped,      
			ds_arquivo_compl,        
			cd_registro, 
			nr_linha) 
		values ( 
			nr_seq_registro_w, 
			ds_arquivo_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			clock_timestamp(), 
			nm_usuario_p, 
			nr_seq_controle_p, 
			ds_compl_arquivo_w, 
			'J930', 
			nr_linha_w);
	end;
