CREATE OR REPLACE PROCEDURE ctb_gerar_interf_k115_ecd (nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, dt_situacao_p timestamp, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


ds_arquivo_w		varchar(4000);
ds_compl_arquivo_w	varchar(4000);
ds_linha_w		varchar(8000);
nr_linha_w		bigint := qt_linha_p;
nr_seq_registro_w	bigint := nr_sequencia_p;
sep_w			varchar(1) := '|';
tp_registro_w		varchar(15) := 'K115';
nr_emp_cod_part_w	smallint := cd_empresa_p;
nr_cond_part_w		smallint;
nr_per_evt_w		double precision;

c01 CURSOR FOR
	SELECT	sit.ie_condicao_empresa,
		sit.pr_evento_empresa
	from	ctb_sit_especial_empresa sit
	where	sit.dt_situacao = dt_situacao_p
	and	sit.cd_empresa = cd_empresa_p
	order by sit.dt_situacao, sit.pr_evento_empresa;

BEGIN

open C01;
loop
fetch C01 into	
	nr_cond_part_w,
	nr_per_evt_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	ds_linha_w	:= substr(sep_w || tp_registro_w  ||
				sep_w || nr_emp_cod_part_w ||
				sep_w || nr_cond_part_w ||
				sep_w || nr_per_evt_w || sep_w,1,8000);

	ds_arquivo_w := substr(ds_linha_w,1,4000);
	ds_compl_arquivo_w := substr(ds_linha_w,4001,4000);
	nr_seq_registro_w := nr_seq_registro_w + 1;
	nr_linha_w := nr_linha_w + 1;

	insert into ctb_sped_registro(
			nr_sequencia,
			ds_arquivo,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_controle_sped,
			ds_arquivo_compl,
			cd_registro,
			nr_linha)
		values (
			nr_seq_registro_w,
			ds_arquivo_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_controle_p,
			ds_compl_arquivo_w,
			tp_registro_w,
			nr_linha_w);

	end;
end loop;
close C01;

commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;

end;
