CREATE OR REPLACE PROCEDURE ctb_gerar_param_conta_contab ( cd_empresa_p bigint, cd_estabelecimento_p bigint, cd_material_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, ie_tipo_conta_p bigint, ie_clinica_p bigint, cd_setor_atendimento_p bigint, ie_classif_convenio_p text, ie_tipo_atendimento_p bigint, ie_tipo_convenio_p bigint, cd_convenio_p bigint, cd_categoria_convenio_p text, cd_local_estoque_p bigint, cd_operacao_estoque_p bigint, dt_vigencia_p timestamp, cd_centro_custo_p bigint, cd_plano_p text, ie_regra_pacote_p text, nm_usuario_p text, nr_seq_proc_interno_p bigint) AS $body$
DECLARE


cd_centro_custo_w			bigint	:= cd_centro_custo_p;
cd_conta_contabil_w			varchar(40);
cd_material_w				bigint	:= cd_material_p;
cd_procedimento_w			bigint	:= cd_procedimento_p;


BEGIN

delete from w_ctb_param_conta_contab
where	nm_usuario	= nm_usuario_p;

if (coalesce(cd_material_w,0) <> 0) then
		
	SELECT * FROM define_conta_material(	cd_estabelecimento_p, cd_material_p, ie_tipo_conta_p, ie_clinica_p, cd_setor_atendimento_p, ie_classif_convenio_p, ie_tipo_atendimento_p, ie_tipo_convenio_p, cd_convenio_p, cd_categoria_convenio_p, cd_local_estoque_p, cd_operacao_estoque_p, dt_vigencia_p, cd_conta_contabil_w, cd_centro_custo_w, cd_plano_p) INTO STRICT cd_conta_contabil_w, cd_centro_custo_w;
end if;

if	((coalesce(cd_procedimento_w,0) <> 0) or (coalesce(nr_seq_proc_interno_p,0) <> 0))then

	SELECT * FROM define_conta_procedimento(cd_estabelecimento_p, cd_procedimento_p, 1, ie_tipo_conta_p, ie_clinica_p, cd_setor_atendimento_p, ie_classif_convenio_p, ie_tipo_atendimento_p, ie_tipo_convenio_p, cd_convenio_p, cd_categoria_convenio_p, dt_vigencia_p, cd_conta_contabil_w, cd_centro_custo_w, cd_plano_p, ie_regra_pacote_p, null, null, null, null, null, null, null, nr_seq_proc_interno_p) INTO STRICT cd_conta_contabil_w, cd_centro_custo_w;
end if;

insert into W_CTB_PARAM_CONTA_CONTAB(
	dt_atualizacao,
	nm_usuario,
	cd_centro_custo,
	cd_conta_contabil)
values (	clock_timestamp(),
	nm_usuario_p,
	cd_centro_custo_w,
	cd_conta_contabil_w);
commit;

end;
$body$
