SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_gerar_tributo ( nr_seq_tributo_p bigint, ie_acao_p text, nm_usuario_p text) AS $body$
DECLARE


/*	ie_acao_p

G	Gerar
D	Desfazer geração

*/
cd_tributo_w		smallint;
cd_estabelecimento_w	smallint;
cd_condicao_pagamento_w	bigint;


BEGIN

if (ie_acao_p	= 'G') then

	select	max(a.cd_condicao_pagamento),
		max(a.cd_tributo),
		max(a.cd_estabelecimento)
	into STRICT	cd_condicao_pagamento_w,
		cd_tributo_w,
		cd_estabelecimento_w
	from	ctb_tributo a
	where	a.nr_sequencia	= nr_seq_tributo_p;

	if (coalesce(cd_condicao_pagamento_w::text, '') = '') then

		select	max(a.cd_cond_pagto)
		into STRICT	cd_condicao_pagamento_w
		from	tributo_conta_pagar a
		where	a.cd_estabelecimento	= cd_estabelecimento_w
		and	a.cd_tributo		= cd_tributo_w;

	end if;

	update	ctb_tributo
	set	dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		dt_geracao		= clock_timestamp(),
		cd_condicao_pagamento	= cd_condicao_pagamento_w
	where	nr_sequencia		= nr_seq_tributo_p;

elsif (ie_acao_p	= 'D') then

	update	ctb_tributo
	set	dt_atualizacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		dt_geracao	 = NULL
	where	nr_sequencia	= nr_seq_tributo_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
