CREATE OR REPLACE PROCEDURE ctb_importar_cenario ( cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_cenario_p bigint, nr_seq_incremento_p bigint, dt_mes_ini_p timestamp, dt_mes_fim_p timestamp, cd_centro_custo_p bigint, cd_conta_contabil_p text, vl_fixo_p bigint, ie_sobrepor_p text, nr_seq_regra_p text) AS $body$
DECLARE



dt_inicial_cen_w			timestamp;
dt_final_cen_w			timestamp;
nr_sequencia_w			bigint;
nr_seq_regra_w			bigint;
ds_consistencia_w			varchar(255);
ie_vigente_w			varchar(1)	:= 'S';

BEGIN

select	nextval('ctb_orc_cen_regra_seq')
into STRICT	nr_sequencia_w
;


select	max(ctb_obter_mes_ref(nr_seq_mes_inicio)),
	max(ctb_obter_mes_ref(nr_seq_mes_fim))
into STRICT	dt_inicial_cen_w,
	dt_final_cen_w
from	ctb_orc_cenario
where	nr_sequencia	= nr_seq_cenario_p;


if (dt_mes_fim_p < dt_mes_ini_p) then

	CALL wheb_mensagem_pck.exibir_mensagem_abort(266540);
end if;
/*OS271616 - Consistir datas...*/

ie_vigente_w	:= substr(ctb_obter_se_vigente(dt_mes_ini_p, dt_inicial_cen_w, dt_final_cen_w),1,1);
if (ie_vigente_w = 'S') then

	ie_vigente_w	:= substr(ctb_obter_se_vigente(dt_mes_fim_p, dt_inicial_cen_w, dt_final_cen_w),1,1);
end if;

if (ie_vigente_w = 'N') then

	/*O período da regra está fora da vigência do cenário.#@#@');*/

	CALL wheb_mensagem_pck.exibir_mensagem_abort(266541);
end if;

if (coalesce(nr_seq_regra_p, '0') = '0') then
	begin

	select	coalesce(max(nr_seq_regra), 0)
	into STRICT	nr_seq_regra_w
	from	ctb_orc_cen_regra
	where	nr_seq_cenario	= nr_seq_cenario_p;

	if (coalesce(nr_seq_incremento_p, 0) = 0) then
		nr_seq_regra_w	:= 1;
	else
		nr_seq_regra_w	:= nr_seq_regra_w + nr_seq_incremento_p;
	end if;

	end;
else
	begin

	if (coalesce(nr_seq_regra_p, '0') <> '0') then

		nr_seq_regra_w	:= nr_seq_regra_p;
	end if;

	end;
end if;


insert into ctb_orc_cen_regra(
	nr_sequencia,
	cd_estabelecimento,
	nr_seq_cenario,
	nr_seq_regra,
	ie_regra_valor,
	dt_mes_inic,
	dt_mes_fim,
	cd_centro_custo,
	cd_conta_contabil,
	vl_fixo,
	ie_sobrepor,
	nm_usuario,
	dt_atualizacao,
	nm_usuario_nrec,
	dt_atualizacao_nrec,
	ie_status_origem)
values (	nr_sequencia_w,
	cd_estabelecimento_p,
	nr_seq_cenario_p,
	nr_seq_regra_w,
	'VF',
	dt_mes_ini_p,
	dt_mes_fim_p,
	cd_centro_custo_p,
	cd_conta_contabil_p,
	vl_fixo_p,
	ie_sobrepor_p,
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	'I');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
