CREATE OR REPLACE PROCEDURE ctb_importar_regra_metrica ( cd_empresa_p bigint, cd_estabelecimento_p bigint, nr_seq_cenario_p bigint, nr_seq_regra_p bigint, nr_seq_metrica_p bigint, nr_seq_incremento_p bigint, cd_centro_custo_p bigint, dt_mes_inic_p timestamp, dt_mes_fim_p timestamp, qt_metrica_p bigint, ie_sobrepor_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_regra_w			bigint;


BEGIN

select	coalesce(max(nr_seq_regra),0)
into STRICT	nr_seq_regra_w
from	ctb_regra_metrica
where	nr_seq_cenario		= nr_seq_cenario_p
and	cd_estabelecimento	= cd_estabelecimento_p;

nr_seq_regra_w	:= nr_seq_regra_w + coalesce(nr_seq_incremento_p,1);

insert into ctb_regra_metrica(
	nr_sequencia,
	cd_estabelecimento,
	nr_seq_cenario,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_regra,
	nr_seq_metrica,
	nr_seq_mes_ref,
	cd_centro_custo,
	dt_mes_inic,
	dt_mes_fim,
	ie_regra,
	pr_aplicar,
	ie_sobrepor,
	qt_fixa,
	qt_leito,
	tx_ocupacao)
values (	nextval('ctb_regra_metrica_seq'),
	cd_estabelecimento_p,
	nr_seq_cenario_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	coalesce(nr_seq_regra_p,0),
	nr_seq_metrica_p,
	null,
	cd_centro_custo_p,
	dt_mes_inic_p,
	dt_mes_fim_p,
	'QF',
	null,
	'S',
	qt_metrica_p,
	null,
	null);



commit;

end;
$body$
