CREATE OR REPLACE PROCEDURE ctb_insere_contab_param_item ( nr_lote_contabil_p bigint, nr_documento_p bigint, nr_seq_paramentro_p bigint, nm_usuario_p text, ie_limpar_item_p text default 'N') AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
cd_tipo_lote_contabil_w		tipo_lote_contabil.cd_tipo_lote_contabil%Type;
qt_registro_w				bigint;


BEGIN

if (ie_limpar_item_p	= 'S') then
	delete	FROM lote_contabil_param_item a
	where	a.nr_lote_contabil		= nr_lote_contabil_p
	and	a.nr_seq_parametro		= nr_seq_paramentro_p
	and	a.nr_documento			= nr_documento_p;

	commit;

else
	cd_tipo_lote_contabil_w	:= (ctb_obter_tipo_lote_contabil(nr_lote_contabil_p,'C'))::numeric;

	select	count(nr_sequencia)
	into STRICT	qt_registro_w
	from	lote_contabil_param_item a
	where	a.nr_lote_contabil		= nr_lote_contabil_p
	and	a.cd_tipo_lote_contabil		= cd_tipo_lote_contabil_w
	and	a.nr_seq_parametro		= nr_seq_paramentro_p
	and	a.nr_documento			= nr_documento_p;

	if (qt_registro_w = 0) then
		begin
		insert into lote_contabil_param_item(
				nr_sequencia,
				nr_lote_contabil,
				cd_tipo_lote_contabil,
				nr_seq_parametro,
				nr_documento,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec)
		values (	nextval('lote_contabil_param_item_seq'),
				nr_lote_contabil_p,
				cd_tipo_lote_contabil_w,
				nr_seq_paramentro_p,
				nr_documento_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p);
		end;
	end if;
end if;

commit;

end;
$body$
