CREATE OR REPLACE PROCEDURE ctb_inserir_conta_concil ( nr_seq_concil_p ctb_registro_concil.nr_sequencia%type, cd_conta_contabil_p conta_contabil.cd_conta_contabil%type, nm_usuario_p text) AS $body$
DECLARE


qt_registro_w	bigint	:= 0;

BEGIN

select	count(nr_sequencia)
into STRICT	qt_registro_w
from	ctb_registro_concil_conta a
where	a.nr_seq_reg_concil	= nr_seq_concil_p
and		a.cd_conta_contabil = cd_conta_contabil_p;

if (qt_registro_w = 0) then

	insert into ctb_registro_concil_conta(
		nr_sequencia,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nm_usuario,
		dt_atualizacao,
		nr_seq_reg_concil,
		cd_conta_contabil,
		qt_total_mov,
		qt_mov_concil,
		qt_mov_pendente)
	values (	nextval('ctb_registro_concil_conta_seq'),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_concil_p,
		cd_conta_contabil_p,
		0,
		0,
		0);
end if;

commit;

end;
$body$
