CREATE OR REPLACE PROCEDURE ctb_inserir_excl_agrup_movto ( nr_seq_movto_p bigint, nr_seq_movto_cp_p bigint, nr_lote_contabil_p bigint, ie_opcao_p text) AS $body$
BEGIN

if (ie_opcao_p = 'I')then
	update	ctb_movimento
