CREATE OR REPLACE PROCEDURE ctb_justificar_acomp_periodo ( nr_seq_mes_ref_p bigint, nr_seq_orcamento_p bigint, ds_justificativa_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE



cd_conta_contabil_w		varchar(20);
cd_centro_custo_w			integer;
ds_justificativa_w			varchar(2000);
nr_sequencia_w			bigint;


BEGIN


ds_justificativa_w			:= substr(ds_justificativa_p, 1, 2000);

select	a.cd_conta_contabil,
	a.cd_centro_custo
into STRICT	cd_conta_contabil_w,
	cd_centro_custo_w
from	w_ctb_orcamento_vis a
where	a.nr_sequencia		= nr_seq_orcamento_p;

select	max(a.nr_sequencia)
into STRICT	nr_sequencia_w
from	ctb_orcamento a
where	a.nr_seq_mes_ref		= nr_seq_mes_ref_p
and	a.cd_conta_contabil	= cd_conta_contabil_w
and	a.cd_centro_custo		= cd_centro_custo_w
and	cd_estabelecimento	= cd_estabelecimento_p;


if (coalesce(nr_sequencia_w, 0) <> 0)then

	update	ctb_orcamento
	set	ds_justificativa	= ds_justificativa_w,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_w;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
