SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE ctb_liberar_lotes ( nr_seq_mes_ref_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


dt_abertura_w		timestamp;
dt_fechamento_w		timestamp;
qt_lote_w		bigint;


BEGIN

select dt_abertura,
	dt_fechamento
into STRICT 	dt_abertura_w,
	dt_fechamento_w
from	ctb_mes_ref
where	nr_sequencia	= nr_seq_mes_ref_p;

if (coalesce(dt_abertura_w::text, '') = '')  then
	/*'O mês não está aberto');*/

	CALL wheb_mensagem_pck.exibir_mensagem_abort(207401);
end if;

if (dt_fechamento_w IS NOT NULL AND dt_fechamento_w::text <> '') then
	/*'O mês está fechado');*/

	CALL wheb_mensagem_pck.exibir_mensagem_abort(207399);
end if;

select	count(*)
into STRICT	qt_lote_w
from	lote_contabil
where	nr_seq_mes_ref	= nr_seq_mes_ref_p
and	cd_estabelecimento	= cd_estabelecimento_p
and	coalesce(dt_atualizacao_saldo::text, '') = ''
and	(dt_processo IS NOT NULL AND dt_processo::text <> '')
and	dt_processo > clock_timestamp() - interval '3 days'/1440;

if (qt_lote_w > 0) then
	/*Existem lotes com processo a menos de 3 minutos'*/

	CALL wheb_mensagem_pck.exibir_mensagem_abort(266560);
end if;

update	lote_contabil
set	dt_processo		 = NULL
where	nr_seq_mes_ref	= nr_seq_mes_ref_p
and	cd_estabelecimento	= cd_estabelecimento_p
and	(dt_processo IS NOT NULL AND dt_processo::text <> '');

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
