CREATE OR REPLACE PROCEDURE ctb_liberar_orcamento_mes ( cd_empresa_p bigint, cd_estabelecimento_p bigint, nr_seq_mes_ref_p bigint, ie_operacao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w				bigint;

/* Registros pendentes para liberação */

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	ctb_orcamento a
where	a.cd_estabelecimento	= coalesce(cd_estabelecimento_p,a.cd_estabelecimento)
and	a.nr_seq_mes_ref	= nr_seq_mes_ref_p
and	coalesce(a.dt_liberacao::text, '') = '';

/* Registros liberados */

c02 CURSOR FOR
SELECT	a.nr_sequencia
from	ctb_orcamento a
where	a.cd_estabelecimento	= cd_estabelecimento_p
and	a.nr_seq_mes_ref	= nr_seq_mes_ref_p
and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');


BEGIN

if (ie_operacao_p	= 'L') then
	begin
	open C01;
	loop
	fetch C01 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update	ctb_orcamento
		set	dt_liberacao	= clock_timestamp(),
			nm_usuario_lib	= nm_usuario_p
		where	nr_sequencia	= nr_sequencia_w;

		end;
	end loop;
	close C01;
	end;
elsif (ie_operacao_p = 'E') then
	begin
	open C02;
	loop
	fetch C02 into
		nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		update	ctb_orcamento
		set	dt_liberacao	 = NULL,
			nm_usuario_lib	= ''
		where	nr_sequencia	= nr_sequencia_w;

		end;
	end loop;
	close C02;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
