CREATE OR REPLACE PROCEDURE ctb_obter_regra_estab_dif ( cd_estabelecimento_p bigint, cd_tipo_lote_contabil_p bigint, cd_estab_origem_p bigint, ie_permite_p INOUT text, cd_conta_contabil_p INOUT text, cd_historico_p INOUT bigint) AS $body$
DECLARE


cd_empresa_w				bigint;
ie_permite_w				varchar(15);
cd_conta_contabil_w			varchar(20);
cd_historico_w				bigint;
cd_estab_origem_w				bigint;

C01 CURSOR FOR
SELECT	ie_permite,
	cd_conta_contabil,
	cd_historico
FROM	ctb_regra_estab_dif
WHERE	cd_empresa		= cd_empresa_w
AND	cd_tipo_lote_contabil	= cd_tipo_lote_contabil_p
AND	(((coalesce(cd_estab_origem, cd_estab_origem_w)	= cd_estab_origem_w)
AND (cd_estab_origem_w > 0))
OR	(((cd_estab_origem = coalesce(cd_estab_origem,cd_estab_origem_p))
and (cd_estab_origem_w = 0))))
AND	coalesce(cd_estabelecimento, cd_estabelecimento_p)	= cd_estabelecimento_p;


BEGIN


ie_permite_w			:= 'N';
cd_conta_contabil_w		:= null;
cd_estab_origem_w			:= coalesce(cd_estab_origem_p,0);

select	max(cd_empresa)
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento		= cd_estabelecimento_p;

OPEN C01;
LOOP
FETCH C01 into
	ie_permite_w,
	cd_conta_contabil_w,
	cd_historico_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ie_permite_w			:= ie_permite_w;

END LOOP;
CLOSE C01;

ie_permite_p				:= ie_permite_w;
cd_conta_contabil_p			:= cd_conta_contabil_w;
cd_historico_p			:= cd_historico_w;

END;
$body$
