CREATE OR REPLACE PROCEDURE ctb_obter_valor_lote_js ( nr_lote_contabil_p bigint, vl_debito_p INOUT text, vl_credito_p INOUT text, vl_debcred_p INOUT text) AS $body$
DECLARE


vl_total_debito_w	double precision := 0.00;
vl_total_credito_w	double precision := 0.00;
vl_debcred_w	double precision := 0.00;


BEGIN
if (nr_lote_contabil_p IS NOT NULL AND nr_lote_contabil_p::text <> '') then
	begin
	select	coalesce(sum(vl_movimento),0)
	into STRICT	vl_total_debito_w
	from	ctb_movimento
	where	nr_lote_contabil = nr_lote_contabil_p
	and	(cd_conta_debito IS NOT NULL AND cd_conta_debito::text <> '');

	select	coalesce(sum(vl_movimento),0)
	into STRICT	vl_total_credito_w
	from	ctb_movimento
	where	nr_lote_contabil = nr_lote_contabil_p
	and	(cd_conta_credito IS NOT NULL AND cd_conta_credito::text <> '');

	if (vl_total_debito_w != vl_total_credito_w) then
		begin
		vl_debcred_w 	:= vl_total_debito_w  - vl_total_credito_w;
		end;
	end if;
	end;
end if;
vl_debito_p	:= campo_mascara_virgula(vl_total_debito_w);
vl_credito_p	:= campo_mascara_virgula(vl_total_credito_w);
vl_debcred_p	:= campo_mascara_virgula(vl_debcred_w);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
