CREATE OR REPLACE PROCEDURE ctb_obter_valor_lote ( nr_lote_contabil_p bigint, vl_debito_p INOUT bigint, vl_credito_p INOUT bigint) AS $body$
DECLARE


vl_total_debito_w		double precision := 0;
vl_total_credito_w		double precision := 0;


BEGIN

select	coalesce(sum(vl_movimento),0)
into STRICT	vl_total_debito_w
from	ctb_movimento
where	nr_lote_contabil = nr_lote_contabil_p
  and	(cd_conta_debito IS NOT NULL AND cd_conta_debito::text <> '');

select	coalesce(sum(vl_movimento),0)
into STRICT	vl_total_credito_w
from	ctb_movimento
where	nr_lote_contabil = nr_lote_contabil_p
and	(cd_conta_credito IS NOT NULL AND cd_conta_credito::text <> '');

vl_debito_p			:= vl_total_debito_w;
vl_credito_p			:= vl_total_credito_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
