		coalesce(ie_tipo_segurado,' '),
		coalesce(ie_benef_remido,' '),
		coalesce(nr_seq_intercambio,0),
		coalesce(nr_seq_grupo_contrato,0),
		coalesce(cd_estab_setor_pessoa,0),
