				vetor_contas_w[nr_vetor_w].nr_id				:= nr_id_w;
				vetor_contas_w[nr_vetor_w].cd_conta_deb_tx_inter_glosa		:= cd_conta_debito_w;
				vetor_contas_w[nr_vetor_w].cd_classif_tx_glosa_cred		:= cd_classificacao_credito_w;
				vetor_contas_w[nr_vetor_w].cd_classif_tx_glosa_deb		:= cd_classificacao_debito_w;
				vetor_contas_w[nr_vetor_w].nr_seq_esquema_tx_inter_glosa	:= nr_seq_esquema_w;
				vetor_contas_w[nr_vetor_w].cd_historico_tx_inter_glosa		:= cd_historico_padrao_w;
				vetor_contas_w[nr_vetor_w].nr_seq_grupo_ans			:= nr_seq_grupo_ans_ww;
			elsif (nr_seq_material_w IS NOT NULL AND nr_seq_material_w::text <> '') then
				if (nr_seq_atualizacao_p IS NOT NULL AND nr_seq_atualizacao_p::text <> '') and (ie_status_conta_w <> 'C') then
					if (coalesce(nr_seq_esquema_w::text, '') = '') then
						ie_status_w		:= 1;
						nr_seq_conta_proc_w	:= null;
						nr_seq_conta_mat_w	:= nr_seq_conta_item_w;
						ie_conta_glosa_ww	:= 'TG';
						ie_inconsistencia_w	:= 'S';
					elsif	((coalesce(cd_conta_credito_w::text, '') = '') or (coalesce(cd_conta_debito_w::text, '') = '')) then
						ie_status_w		:= 2;
						nr_seq_conta_proc_w	:= null;
						nr_seq_conta_mat_w	:= nr_seq_conta_item_w;
						ie_conta_glosa_ww	:= 'TG';
						ie_inconsistencia_w	:= 'S';
					end if;
				end if;
				
				nr_vetor_w	:= null;
				
				for i in 1..vetor_contas_w.count loop
					if (vetor_contas_w[i].nr_seq_conta_item = nr_seq_conta_item_w) then
						nr_vetor_w	:= i;
						exit;
					end if;
				end loop;
				
				if (coalesce(nr_vetor_w::text, '') = '') then
					nr_vetor_w	:= vetor_contas_w.count + 1;
				end if;
				/*Armazenar os valores no vetor*/

				vetor_contas_w[nr_vetor_w].nr_seq_conta_item			:= nr_seq_conta_item_w;
				vetor_contas_w[nr_vetor_w].ie_tipo_conta			:= 'M';
				vetor_contas_w[nr_vetor_w].cd_conta_cred_tx_inter_glosa		:= cd_conta_credito_w;
				vetor_contas_w[nr_vetor_w].nr_id				:= nr_id_w;
				vetor_contas_w[nr_vetor_w].cd_conta_deb_tx_inter_glosa		:= cd_conta_debito_w;
				vetor_contas_w[nr_vetor_w].cd_classif_tx_glosa_cred		:= cd_classificacao_credito_w;
				vetor_contas_w[nr_vetor_w].cd_classif_tx_glosa_deb		:= cd_classificacao_debito_w;
				vetor_contas_w[nr_vetor_w].nr_seq_esquema_tx_inter_glosa	:= nr_seq_esquema_w;
				vetor_contas_w[nr_vetor_w].cd_historico_tx_inter_glosa		:= cd_historico_padrao_w;
				vetor_contas_w[nr_vetor_w].nr_seq_grupo_ans			:= nr_seq_grupo_ans_ww;
			end if;
		end if;
		
		if (ie_inconsistencia_w = 'S') then
			CALL pls_gravar_mov_contabil(nr_seq_atualizacao_p,
						ie_status_w,
						null,
						nr_seq_conta_proc_w,
						nr_seq_conta_mat_w,
						ie_conta_glosa_ww,
						null,
						null,
						null,
						null,
						null,
						null,
						null,
						null,
						null,
						null,
						null,
						nm_usuario_p,
						nr_seq_esquema_w);
		end if;
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

for i in 1..vetor_contas_w.count loop
	if (vetor_contas_w[i].ie_tipo_conta = 'P') then
	
		qt_movimento_w	:= qt_movimento_w + 1;
	
		update	pls_conta_proc
		set	cd_conta_cred			= vetor_contas_w[i].cd_conta_cred,
			cd_conta_deb			= vetor_contas_w[i].cd_conta_deb,
			cd_classif_cred			= vetor_contas_w[i].cd_classif_cred,
			cd_classif_deb			= vetor_contas_w[i].cd_classif_deb,
			nr_seq_esquema			= vetor_contas_w[i].nr_seq_esquema,
			cd_historico			= vetor_contas_w[i].cd_historico,
			cd_conta_glosa_cred		= CASE WHEN ie_status_prov_pagto_w='F' THEN  cd_conta_glosa_cred  ELSE vetor_contas_w[i].cd_conta_glosa_cred END ,
			cd_conta_glosa_deb		= CASE WHEN ie_status_prov_pagto_w='F' THEN  cd_conta_glosa_deb  ELSE vetor_contas_w[i].cd_conta_glosa_deb END ,
			cd_classif_glosa_cred		= CASE WHEN ie_status_prov_pagto_w='F' THEN  cd_classif_glosa_cred  ELSE vetor_contas_w[i].cd_classif_glosa_cred END ,
			cd_classif_glosa_deb		= CASE WHEN ie_status_prov_pagto_w='F' THEN  cd_classif_glosa_deb  ELSE vetor_contas_w[i].cd_classif_glosa_deb END ,
			nr_seq_esquema_glosa		= CASE WHEN ie_status_prov_pagto_w='F' THEN  nr_seq_esquema_glosa  ELSE vetor_contas_w[i].nr_seq_esquema_glosa END ,
			cd_historico_glosa		= CASE WHEN ie_status_prov_pagto_w='F' THEN  cd_historico_glosa  ELSE vetor_contas_w[i].cd_historico_glosa END ,
			nr_seq_grupo_ans		= vetor_contas_w[i].nr_seq_grupo_ans,
			cd_conta_cred_tx_inter		= vetor_contas_w[i].cd_conta_cred_tx_inter,
			cd_conta_deb_tx_inter		= vetor_contas_w[i].cd_conta_deb_tx_inter,
			cd_classif_tx_inter_cred	= vetor_contas_w[i].cd_classif_tx_inter_cred,
			cd_classif_tx_inter_deb		= vetor_contas_w[i].cd_classif_tx_inter_deb,
			nr_seq_esquema_tx_inter		= vetor_contas_w[i].nr_seq_esquema_tx_inter,
			cd_historico_tx_inter		= vetor_contas_w[i].cd_historico_tx_inter,
			cd_conta_cred_tx_inter_glosa	= vetor_contas_w[i].cd_conta_cred_tx_inter_glosa,
			cd_conta_deb_tx_inter_glosa	= vetor_contas_w[i].cd_conta_deb_tx_inter_glosa,
			cd_classif_tx_glosa_cred	= vetor_contas_w[i].cd_classif_tx_glosa_cred,
			cd_classif_tx_glosa_deb		= vetor_contas_w[i].cd_classif_tx_glosa_deb,
			nr_seq_esquema_tx_inter_glosa	= vetor_contas_w[i].nr_seq_esquema_tx_inter_glosa,
			cd_historico_tx_inter_glosa	= vetor_contas_w[i].cd_historico_tx_inter_glosa,
			cd_historico_ajuste		= CASE WHEN ie_lote_ajuste_prod_w='R' THEN  vetor_contas_w[i].cd_historico_ajuste  ELSE cd_historico_ajuste END
		where	rowid				= vetor_contas_w[i].nr_id;
		
		if (mod(qt_movimento_w, 200) = 0) then
			commit;
		end if;
		
	elsif (vetor_contas_w[i].ie_tipo_conta = 'M') then
	
		qt_movimento_w	:= qt_movimento_w + 1;
	
		update	pls_conta_mat
		set	cd_conta_cred			= vetor_contas_w[i].cd_conta_cred,
			cd_conta_deb			= vetor_contas_w[i].cd_conta_deb,
			cd_classif_cred			= vetor_contas_w[i].cd_classif_cred,
			cd_classif_deb			= vetor_contas_w[i].cd_classif_deb,
			nr_seq_esquema			= vetor_contas_w[i].nr_seq_esquema,
			cd_historico			= vetor_contas_w[i].cd_historico,
			cd_conta_glosa_cred		= CASE WHEN ie_status_prov_pagto_w='F' THEN  cd_conta_glosa_cred  ELSE vetor_contas_w[i].cd_conta_glosa_cred END ,
			cd_conta_glosa_deb		= CASE WHEN ie_status_prov_pagto_w='F' THEN  cd_conta_glosa_deb  ELSE vetor_contas_w[i].cd_conta_glosa_deb END ,
			cd_classif_glosa_cred		= CASE WHEN ie_status_prov_pagto_w='F' THEN  cd_classif_glosa_cred  ELSE vetor_contas_w[i].cd_classif_glosa_cred END ,
			cd_classif_glosa_deb		= CASE WHEN ie_status_prov_pagto_w='F' THEN  cd_classif_glosa_deb  ELSE vetor_contas_w[i].cd_classif_glosa_deb END ,
			nr_seq_esquema_glosa		= CASE WHEN ie_status_prov_pagto_w='F' THEN  nr_seq_esquema_glosa  ELSE vetor_contas_w[i].nr_seq_esquema_glosa END ,
			cd_historico_glosa		= CASE WHEN ie_status_prov_pagto_w='F' THEN  cd_historico_glosa  ELSE vetor_contas_w[i].cd_historico_glosa END ,
			nr_seq_grupo_ans		= vetor_contas_w[i].nr_seq_grupo_ans,
			cd_conta_cred_tx_inter		= vetor_contas_w[i].cd_conta_cred_tx_inter,
			cd_conta_deb_tx_inter		= vetor_contas_w[i].cd_conta_deb_tx_inter,
			cd_classif_tx_inter_cred	= vetor_contas_w[i].cd_classif_tx_inter_cred,
			cd_classif_tx_inter_deb		= vetor_contas_w[i].cd_classif_tx_inter_deb,
			nr_seq_esquema_tx_inter		= vetor_contas_w[i].nr_seq_esquema_tx_inter,
			cd_historico_tx_inter		= vetor_contas_w[i].cd_historico_tx_inter,
			cd_conta_cred_tx_inter_glosa	= vetor_contas_w[i].cd_conta_cred_tx_inter_glosa,
			cd_conta_deb_tx_inter_glosa	= vetor_contas_w[i].cd_conta_deb_tx_inter_glosa,
			cd_classif_tx_glosa_cred	= vetor_contas_w[i].cd_classif_tx_glosa_cred,
			cd_classif_tx_glosa_deb		= vetor_contas_w[i].cd_classif_tx_glosa_deb,
			nr_seq_esquema_tx_inter_glosa	= vetor_contas_w[i].nr_seq_esquema_tx_inter_glosa,
			cd_historico_tx_inter_glosa	= vetor_contas_w[i].cd_historico_tx_inter_glosa,
			cd_historico_ajuste		= CASE WHEN ie_lote_ajuste_prod_w='R' THEN  vetor_contas_w[i].cd_historico_ajuste  ELSE cd_historico_ajuste END
		where	rowid				= vetor_contas_w[i].nr_id;
		
		if (mod(qt_movimento_w, 200) = 0) then
			commit;
		end if;
	
	end if;
	
end loop;

qt_movimento_p	:= qt_movimento_w;

end;
