			from	titulo_receber
			where	nr_seq_mensalidade	= nr_seq_mensalidade_w;
			
			if (coalesce(nr_titulo_w,0) = 0) then
				begin
					
				select	max(nr_titulo)
				into STRICT	nr_titulo_w
				from	titulo_receber d,
					pls_mensalidade c,
					pls_mensalidade_segurado b,
