						into STRICT	ds_grupo_w
						from	pls_contab_mov_mens_lote	b,
							pls_esquema_contabil		a
						where	b.nr_sequencia	= a.nr_seq_movimentacao
						and	a.nr_sequencia	= nr_seq_esquema_w;
