CREATE OR REPLACE PROCEDURE ctb_pls_fechar_competencia_sus ( nr_seq_processo_comp_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
BEGIN

if (ie_opcao_p = 'F') then
	update	pls_processo_competencia
	set	dt_fechamento = clock_timestamp(),
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where nr_sequencia = nr_seq_processo_comp_p;
elsif (ie_opcao_p = 'A') then
	update	pls_processo_competencia
	set	dt_fechamento  = NULL,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where nr_sequencia = nr_seq_processo_comp_p;
end if;

commit;

end;
$body$
