CREATE OR REPLACE PROCEDURE ctb_pls_obter_conta_ressa ( cd_estabelecimento_p bigint, dt_referencia_p timestamp, ie_debito_credito_p text, ie_tipo_contratacao_p text, ie_preco_p text, ie_segmentacao_p text, ie_regulamentacao_p text, ie_participacao_p text, ie_tipo_beneficiario_p text, ie_tipo_segurado_p text, nr_seq_regra_p INOUT bigint, cd_historico_p INOUT bigint, cd_conta_contabil_p INOUT text, cd_historico_deferido_p INOUT bigint, cd_conta_deferido_p INOUT text) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter a "Regra contábil de despesas com ressarcimento", fuunção OPS - Critérios
Contábeis
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
	CTB_PLS_ATUALIZAR_RESSA
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_regra_w			varchar(20);
cd_conta_contabil_w		varchar(20);
cd_conta_deferido_w		varchar(20);
cd_historico_w			bigint;
cd_historico_deferido_w		bigint;

c_regra CURSOR FOR
	SELECT	nr_sequencia,
		cd_conta_contabil,
		cd_historico,
		cd_conta_deferido,
		cd_historico_deferido
	from	pls_regra_ctb_mensal_ressa
	where	cd_estabelecimento										= cd_estabelecimento_p
	and	dt_inicio_vigencia											<= dt_referencia_p
	and	coalesce(dt_fim_vigencia,dt_referencia_p)						>= dt_referencia_p
	and (coalesce(ie_tipo_contratacao,coalesce(ie_tipo_contratacao_p, 'X'))	= coalesce(ie_tipo_contratacao_p, 'X'))
	and (coalesce(ie_debito_credito,ie_debito_credito_p)					= ie_debito_credito_p)
	and (coalesce(ie_preco,coalesce(ie_preco_p, 'X')) 						= coalesce(ie_preco_p, 'X'))
	and (coalesce(ie_segmentacao,coalesce(ie_segmentacao_p, 'X'))				= coalesce(ie_segmentacao_p, 'X'))
	and (coalesce(ie_regulamentacao,coalesce(ie_regulamentacao_p, 'X'))		= coalesce(ie_regulamentacao_p, 'X'))
	and (coalesce(ie_participacao,coalesce(ie_participacao_p, 'X')) 			= coalesce(ie_participacao_p, 'X'))
	and (coalesce(ie_tipo_beneficiario,coalesce(ie_tipo_beneficiario_p, 'X')) = coalesce(ie_tipo_beneficiario_p, 'X'))
	and (coalesce(ie_tipo_segurado,coalesce(ie_tipo_segurado_p,'X')) 			= coalesce(ie_tipo_segurado_p,'X'))
	order by
		coalesce(ie_regulamentacao, 'A'),
		coalesce(ie_preco, 'A'),
		coalesce(ie_tipo_contratacao, 'A'),
		coalesce(ie_participacao, 'A'),
		coalesce(ie_tipo_beneficiario, 'A'),
		coalesce(ie_segmentacao, 'A'),
		coalesce(ie_debito_credito, 'A'),
		coalesce(ie_tipo_contratacao, 'A'),
		coalesce(cd_conta_contabil,' A'),
		coalesce(dt_inicio_vigencia, clock_timestamp()),
		coalesce(dt_fim_vigencia, clock_timestamp()),
		coalesce(nr_sequencia, 0);


BEGIN

open c_regra;
loop
fetch c_regra into
	nr_seq_regra_w,
	cd_conta_contabil_w,
	cd_historico_w,
	cd_conta_deferido_w,
	cd_historico_deferido_w;
EXIT WHEN NOT FOUND; /* apply on c_regra */
end loop;
close c_regra;

cd_conta_contabil_p		:= coalesce(cd_conta_contabil_w, '0');
nr_seq_regra_p			:= coalesce(nr_seq_regra_w, 0);
cd_historico_p			:= coalesce(cd_historico_w, 0);
cd_conta_deferido_p		:= coalesce(cd_conta_deferido_w, '0');
cd_historico_deferido_p		:= coalesce(cd_historico_deferido_w, 0);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
