			vl_ato_cooperado_aux_w		:= s_array[z].vl_ato_auxiliar;
			vl_ato_nao_cooperado_w		:= s_array[z].vl_ato_nao_cooperado;
			nr_seq_tipo_lanc_w		:= s_array[z].nr_seq_tipo_lanc;
			nr_seq_pagador_w		:= s_array[z].nr_seq_pagador;
