CREATE OR REPLACE PROCEDURE ctb_tirar_cc_patrimonial ( nr_lote_contabil_p bigint) AS $body$
DECLARE



nr_sequencia_w		bigint;
ie_centro_debito_w		varchar(01);
ie_centro_credito_w		varchar(01);

C01 CURSOR FOR
	SELECT	nr_sequencia,
		substr(obter_ie_centro_conta(a.cd_conta_debito),1,1) ie_centro_debito,
		substr(obter_ie_centro_conta(a.cd_conta_credito),1,1)  ie_centro_credito
	from	ctb_movimento a
	where	a.nr_lote_contabil	= nr_lote_contabil_p
	and	exists (
		SELECT 1
		from ctb_movto_centro_custo b
		where a.nr_sequencia	= b.nr_seq_movimento);


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	nr_sequencia_w,
	ie_centro_debito_w,
	ie_centro_credito_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	if (ie_centro_debito_w = 'N') and (ie_centro_credito_w = 'N') then
		delete from ctb_movto_centro_custo a
		where	nr_seq_movimento 	= nr_sequencia_w;
	end if;
END LOOP;
CLOSE C01;
commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
