CREATE OR REPLACE PROCEDURE ctb_update_establishment ( ie_todo_lote_p ctb_movimento.ie_transitorio%TYPE ,nr_sequencia_p ctb_movimento.nr_sequencia%TYPE ,cd_estabelecimento_p ctb_movimento.cd_estabelecimento%TYPE ,nr_lote_contabil_p ctb_movimento.nr_lote_contabil%TYPE ) AS $body$
BEGIN
	IF (ie_todo_lote_p = 'S') THEN
		UPDATE ctb_movimento SET cd_estabelecimento = cd_estabelecimento_p WHERE nr_lote_contabil = nr_lote_contabil_p;
	ELSIF (ie_todo_lote_p = 'N') THEN
		UPDATE ctb_movimento SET cd_estabelecimento = cd_estabelecimento_p WHERE nr_sequencia = nr_sequencia_p;
	END IF;
END;
