CREATE OR REPLACE PROCEDURE ctb_vincular_evento_oper ( cd_operacao_estoque_p bigint, cd_tipo_lote_contabil_p bigint, cd_evento_p bigint, ie_operacao_p text, nm_usuario_p text) AS $body$
BEGIN

if (coalesce(ie_operacao_p, 'X') = 'D') then
	begin

	update 	operacao_estoque set 	cd_evento 				 = NULL,
									cd_tipo_lote_contabil 	 = NULL,
									dt_atualizacao 			= clock_timestamp(),
									nm_usuario 				= nm_usuario_p
	where	cd_operacao_estoque = cd_operacao_estoque_p;

	end;
elsif (coalesce(ie_operacao_p, 'X') = 'V') then
	begin

	update 	operacao_estoque set 	cd_evento 				= cd_evento_p,
									cd_tipo_lote_contabil 	= cd_tipo_lote_contabil_p,
									dt_atualizacao 			= clock_timestamp(),
									nm_usuario 				= nm_usuario_p
	where	cd_operacao_estoque = cd_operacao_estoque_p;

	end;
end if;


commit;

end;
