CREATE OR REPLACE PROCEDURE ctb_vincular_planej_gpi_proj ( nr_sequencia_p ctb_planej_orc_material.nr_sequencia%type, nr_seq_proj_gpi_p ctb_planej_orc_material.nr_seq_proj_gpi%type, ie_tipo_p text, nm_usuario_p text) AS $body$
BEGIN
  if (ie_tipo_p = 'M') then
    update ctb_planej_orc_material a
