           a.dt_atualizacao  = clock_timestamp(),
           a.nm_usuario      = nm_usuario_p
    where  a.nr_sequencia = nr_sequencia_p;
  elsif (ie_tipo_p = 'V') then
    update ctb_planej_orc_valor a
    set    a.nr_seq_proj_gpi = CASE WHEN nr_seq_proj_gpi_p=0 THEN null  ELSE nr_seq_proj_gpi_p END ,
           a.dt_atualizacao  = clock_timestamp(),
           a.nm_usuario      = nm_usuario_p
    where  a.nr_sequencia = nr_sequencia_p;
  end if;

  commit;

end;
