CREATE OR REPLACE PROCEDURE ctrl_irrig_dialise_perit_auto (nr_prescricao_p bigint, nr_seq_solucao_p bigint, ie_controle_p text, dt_controle_p timestamp, qt_volume_infusao_p bigint, qt_volume_drenagem_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_evento_w		bigint;
nr_etapa_evento_w	smallint;


BEGIN 
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_solucao_p IS NOT NULL AND nr_seq_solucao_p::text <> '') and (ie_controle_p IS NOT NULL AND ie_controle_p::text <> '') and (dt_controle_p IS NOT NULL AND dt_controle_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
 
	update	prescr_solucao 
	set	ie_status		= ie_controle_p, 
		dt_status 		= dt_controle_p 
	where	nr_prescricao 	= nr_prescricao_p 
	and	nr_seq_solucao 	= nr_seq_solucao_p;
	 
	select	obter_etapa_evento_dp_adep(nr_prescricao_p,nr_seq_solucao_p,'DPA',ie_controle_p) 
	into STRICT	nr_etapa_evento_w 
	;
 
	select	nextval('hd_prescricao_evento_seq') 
	into STRICT	nr_seq_evento_w 
	;
 
	insert into hd_prescricao_evento( 
		nr_sequencia, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		dt_atualizacao, 
		nm_usuario, 
		nr_prescricao, 
		nr_seq_solucao, 
		nr_etapa_evento, 
		ie_evento, 
		dt_evento, 
		cd_pessoa_evento, 
		qt_volume, 
		qt_vol_drenagem) 
	values ( 
		nr_seq_evento_w, 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		nr_prescricao_p, 
		nr_seq_solucao_p, 
		nr_etapa_evento_w, 
		ie_controle_p, 
		dt_controle_p, 
		substr(obter_dados_usuario_opcao(nm_usuario_p,'C'),1,10), 
		qt_volume_infusao_p, 
		qt_volume_drenagem_p);
		 
	if (ie_controle_p = 'DPAT') and (qt_volume_infusao_p IS NOT NULL AND qt_volume_infusao_p::text <> '') and (qt_volume_drenagem_p IS NOT NULL AND qt_volume_drenagem_p::text <> '') then 
		CALL encerrar_ctrl_irrig_dp(nr_prescricao_p, nr_seq_solucao_p, dt_controle_p, nm_usuario_p);
	elsif (ie_controle_p = 'DPAT') then 
		--v É necessária a informação referente aos volumes de infusão e de drenagem para o encerramento de uma DPA. #@#@ 
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(264593);
	end if;
 
end if;
 
commit;
 
end;
$body$
