SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cus_amortizar_orc ( cd_estabelecimento_p bigint, cd_tabela_custo_p bigint, nr_seq_tabela_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_centro_controle_w	integer := 0;
cd_natureza_gasto_w	numeric(20) := 0;
ie_tipo_gasto_w		varchar(2);
vl_amortizar_w		double precision := 0;
vl_mensal_w		double precision := 0;
dt_referencia_w		timestamp;
nr_sequencia_w		bigint;
vl_avista_w		double precision;
qt_reg_w		bigint;
nr_seq_amort_w		bigint;
nr_seq_ng_w		bigint;
qt_reg_orc_w		bigint;
cd_estabelecimento_w	tabela_custo.cd_estabelecimento%type;
cd_empresa_w		tabela_custo.cd_empresa%type;
cd_estab_amortizacao_w	cus_amortizacao.cd_estabelecimento%type;

c01 CURSOR FOR
SELECT	nr_sequencia,
	cd_centro_controle,
	cd_natureza_gasto,
	nr_seq_ng,
	vl_amortizar,
	0 vl_mensal,
	cd_estabelecimento
from	cus_amortizacao
where	cd_estabelecimento 				= coalesce(cd_estabelecimento_w, cd_estabelecimento)
and	obter_empresa_estab(cd_estabelecimento)		= cd_empresa_w
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	dt_referencia					= dt_referencia_w

union all

SELECT	nr_sequencia,
	cd_centro_controle,
	cd_natureza_gasto,
	nr_seq_ng,
	0,
	vl_prim_mes,
	cd_estabelecimento
from	cus_amortizacao
where	cd_estabelecimento 				= coalesce(cd_estabelecimento_w, cd_estabelecimento)
and	obter_empresa_estab(cd_estabelecimento)		= cd_empresa_w
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	dt_prim_mes					= dt_referencia_w

union all

select	nr_sequencia,
	cd_centro_controle,
	cd_natureza_gasto,
	nr_seq_ng,
	0,
	vl_mensal,
	cd_estabelecimento
from	cus_amortizacao
where	cd_estabelecimento 				= coalesce(cd_estabelecimento_w, cd_estabelecimento)
and	obter_empresa_estab(cd_estabelecimento)		= cd_empresa_w
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	dt_prim_mes					<> dt_referencia_w
and	dt_referencia_w between  pkg_date_utils.add_month(dt_prim_mes,1,0) and pkg_date_utils.add_month(dt_prim_mes, qt_mes - 1,0);


BEGIN

select	dt_mes_referencia,
	cd_estabelecimento,
	cd_empresa
into STRICT	dt_referencia_w,
	cd_estabelecimento_w,
	cd_empresa_w
from	tabela_custo
where	nr_sequencia	= nr_seq_tabela_p;

delete	from cus_amort_mensal
where	nr_seq_tabela	= nr_seq_tabela_p;

open c01;
loop
fetch c01 into
	nr_seq_amort_w,
	cd_centro_controle_w,
	cd_natureza_gasto_w,
	nr_seq_ng_w,
	vl_amortizar_w,
	vl_mensal_w,
	cd_estab_amortizacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	select	coalesce(sum(vl_avista),0),
		count(*)
	into STRICT	vl_avista_w,
		qt_reg_w
	from	orcamento_custo
	where	nr_seq_tabela		= nr_seq_tabela_p
	and	cd_estabelecimento	= cd_estab_amortizacao_w
	and	cd_centro_controle	= cd_centro_controle_w
	and	nr_seq_ng		= nr_seq_ng_w;

	if (vl_amortizar_w > 0) then
		begin
		if (vl_avista_w < vl_amortizar_w) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(244212,'CD_CENTRO_CONTROLE_W=' || cd_centro_controle_w || ';' || 'NR_SEQ_NG_W='|| nr_seq_ng_w );
		else
			update	orcamento_custo
	   		set	vl_avista		= vl_avista - vl_amortizar_w,
				nm_usuario	= nm_usuario_p,
				dt_atualizacao	= clock_timestamp()
			where 	cd_estabelecimento	= cd_estab_amortizacao_w
			and	nr_seq_tabela		= nr_seq_tabela_p
			and	cd_centro_controle	= cd_centro_controle_w
			and	nr_seq_ng		= nr_seq_ng_w;
		end if;
		end;
	else
		begin
		if (qt_reg_w = 1) then
			update	orcamento_custo
   			set	vl_avista		= vl_avista + vl_mensal_w,
				nm_usuario	= nm_usuario_p,
				dt_atualizacao	= clock_timestamp()
			where	cd_estabelecimento	= cd_estab_amortizacao_w
			and	nr_seq_tabela		= nr_seq_tabela_p
			and	cd_centro_controle	= cd_centro_controle_w
			and	nr_seq_ng		= nr_seq_ng_w;
		else
			ie_tipo_gasto_w	:= substr(cus_obter_tipo_gasto_centro(cd_estab_amortizacao_w, cd_centro_controle_w, null, cd_natureza_gasto_w, nr_seq_ng_w),1,1);
			if (ie_tipo_gasto_w in ('V','F','R')) then
			
				select	count(1)
				into STRICT	qt_reg_orc_w
				from	orcamento_custo
				where	cd_estabelecimento = cd_estab_amortizacao_w
				and	CD_TABELA_CUSTO    = cd_tabela_custo_p
				and	cd_centro_controle = cd_centro_controle_w
				and	nr_seq_ng	   = nr_seq_ng_w
				and	ie_tipo_gasto	   = ie_tipo_gasto_w;
				
				if (qt_reg_orc_w = 0) then
			
					insert  into orcamento_custo(
						nr_sequencia,
						cd_estabelecimento,
						cd_tabela_custo,
						nr_seq_tabela,
						cd_centro_controle,
						cd_natureza_gasto,
						dt_atualizacao,
						nm_usuario,
						vl_orcado,
						vl_avista,
						vl_distribuido,
						vl_receb_distrib,
						qt_dias_prazo,
						vl_a_distribuir,
						ie_tipo_gasto,
						nr_seq_ng)
					values (	nextval('orcamento_custo_seq'),
						cd_estab_amortizacao_w,
						cd_tabela_custo_p,
						nr_seq_tabela_p,
						cd_centro_controle_w,
						cd_natureza_gasto_w,
						clock_timestamp(),
						nm_usuario_p,
						0,
						vl_mensal_w,
						0,
						0,
						0,
						0,
						ie_tipo_gasto_w,
						nr_seq_ng_w);
				
				else

					update	orcamento_custo
					set	nr_seq_tabela	= nr_seq_tabela_p,
						nm_usuario	= nm_usuario_p,
						dt_atualizacao	= clock_timestamp(),
						vl_avista	= vl_mensal_w,
						qt_dias_prazo	= 30
					where 	cd_estabelecimento	= cd_estab_amortizacao_w
					and	CD_TABELA_CUSTO    	= cd_tabela_custo_p
					and	cd_centro_controle	= cd_centro_controle_w
					and	nr_seq_ng		= nr_seq_ng_w;
				
				end if;
			end if;
		end if;	
		end;
	end if;
	select	nextval('cus_amort_mensal_seq')
	into STRICT	nr_sequencia_w
	;
	insert into	cus_amort_mensal(
		nr_sequencia,
		nr_seq_amortizacao,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_estabelecimento,
		cd_tabela_custo,
		dt_referencia,
		vl_amortizar,
		vl_mensal,
		nr_seq_tabela)
	values (	nr_sequencia_w,
		nr_seq_amort_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),		
		nm_usuario_p,
		cd_estab_amortizacao_w,
		cd_tabela_custo_p,
		dt_referencia_w,
		vl_amortizar_w,
		vl_mensal_w,
		nr_seq_tabela_p);
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE cus_amortizar_orc ( cd_estabelecimento_p bigint, cd_tabela_custo_p bigint, nr_seq_tabela_p bigint, nm_usuario_p text) FROM PUBLIC;

