CREATE OR REPLACE PROCEDURE cus_calcular_perc_distr ( cd_estabelecimento_p bigint, nr_seq_criterio_p bigint) AS $body$
DECLARE


cd_centro_controle_w		bigint;
cd_estabelecimento_w		bigint;
pr_distribuicao_w			double precision;
qt_peso_w			double precision;
qt_peso_total_w			double precision;
pr_total_w			double precision;
qt_registro_w			bigint;
qt_registro_ww			bigint;

C01 CURSOR FOR
SELECT	cd_estabelecimento,
	cd_centro_controle_dest,
	(qt_distribuicao * coalesce(qt_peso,1))
from	Criterio_Distr_Orc_dest
where	cd_sequencia_criterio	= nr_seq_criterio_p
and	qt_peso_total_w		> 0
order	by qt_distribuicao;


BEGIN

select sum(qt_distribuicao * coalesce(qt_peso,1)),
	count(*)
into STRICT	qt_peso_total_w,
	qt_registro_ww
from	Criterio_Distr_Orc_dest
where	cd_sequencia_criterio	= nr_seq_criterio_p;

qt_registro_w				:= 0;
pr_total_w				:= 0;
OPEN C01;
LOOP
FETCH C01 into
	cd_estabelecimento_w,
	cd_centro_controle_w,
	qt_peso_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	qt_registro_w			:= qt_registro_w + 1;
	if (qt_registro_w = qt_registro_ww) then
		pr_distribuicao_w		:= 100 - pr_total_w;
	else
		pr_distribuicao_w		:= dividir(qt_peso_w * 100, qt_peso_total_w);
	end if;
	pr_total_w			:= pr_total_w + pr_distribuicao_w;
	update	criterio_distr_orc_dest
	set	pr_distribuicao		= pr_distribuicao_w
	where	cd_estabelecimento		= cd_estabelecimento_w
	and	cd_sequencia_criterio	= nr_seq_criterio_p
	and	cd_centro_controle_dest	= cd_centro_controle_w;
END LOOP;
CLOSE C01;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
