CREATE OR REPLACE PROCEDURE cus_calcular_result_ind_sql ( nr_seq_indicador_p bigint, cd_tabela_custo_p bigint, cd_estabelecimento_p bigint, cd_centro_controle_p bigint, dt_referencia_p timestamp, qt_indicador_p INOUT bigint, nr_seq_tabela_p bigint) AS $body$
DECLARE


dt_inicial_w			timestamp;
dt_final_w			timestamp;
dt_referencia_w			timestamp;
ds_parametros_sql_w		varchar(255);
ds_sql_w			varchar(4000);
nr_seq_regra_calc_w		bigint;
qt_real_w			double precision;
cd_empresa_w			integer;


BEGIN

dt_referencia_w	:= trunc(dt_referencia_p,'mm');
dt_inicial_w	:= dt_referencia_w;
dt_final_w	:= fim_mes(dt_referencia_w);
cd_empresa_w	:= obter_empresa_estab(cd_estabelecimento_p);

if (coalesce(dt_referencia_p::text, '') = '') and (coalesce(nr_seq_tabela_p,0) > 0) then
	begin
	select	max(dt_mes_referencia)
	into STRICT	dt_referencia_w
	from	tabela_custo
	where	nr_sequencia = nr_seq_tabela_p;

	if (coalesce(dt_referencia_w::text, '') = '') then
		begin
		select	max(dt_mes_referencia)
		into STRICT	dt_referencia_w
		from	tabela_custo
		where	cd_estabelecimento	= cd_estabelecimento_p
		and	cd_tabela_custo		= cd_tabela_custo_p;
		end;
	end if;
	end;
end if;

if (coalesce(dt_referencia_p::text, '') = '') and (cd_tabela_custo_p IS NOT NULL AND cd_tabela_custo_p::text <> '') then
	begin
	select	max(dt_mes_referencia)
	into STRICT	dt_referencia_w
	from	tabela_custo
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	cd_tabela_custo	= cd_tabela_custo_p;
	end;
end if;

/* Obter o comando SQL*/

select	max(nr_sequencia)
into STRICT	nr_seq_regra_calc_w
from	resultado_indicador_calc
where	nr_seq_indicador	= nr_seq_indicador_p
and	dt_inicio_vigencia <= dt_referencia_w
and	coalesce(dt_fim_vigencia, dt_referencia_w) >= dt_referencia_w;

select	max(ds_sql)
into STRICT	ds_sql_w
from	resultado_indicador_calc
where	nr_sequencia	= nr_seq_regra_calc_w;

if	((position(':DT_REFERENCIA' in upper(ds_sql_w))) > 0) then
	begin
	ds_parametros_sql_w	:= 'dt_referencia=' || dt_referencia_w || ';';
	end;
end if;
if	((position(':DT_INICIAL' in upper(ds_sql_w))) > 0) then
	begin
	ds_parametros_sql_w	:= ds_parametros_sql_w || 'dt_inicial=' || dt_inicial_w || ';';
	end;
end if;
if	((position(':DT_FINAL' in upper(ds_sql_w))) > 0) then
	begin
	ds_parametros_sql_w	:= ds_parametros_sql_w || 'dt_final=' || dt_final_w || ';';
	end;
end if;
if	((position(':CD_ESTABELECIMENTO' in upper(ds_sql_w))) > 0) then
	begin
	ds_parametros_sql_w	:= ds_parametros_sql_w || 'cd_estabelecimento=' || cd_estabelecimento_p || ';';
	end;
end if;
if	((position(':CD_EMPRESA' in upper(ds_sql_w))) > 0) then
	begin
	ds_parametros_sql_w	:= ds_parametros_sql_w || 'cd_empresa=' || cd_empresa_w || ';';
	end;
end if;
if	((position(':CD_CENTRO_CONTROLE' in upper(ds_sql_w))) > 0) then
	begin
	ds_parametros_sql_w	:= ds_parametros_sql_w || 'cd_centro_controle=' || cd_centro_controle_p || ';';
	end;
end if;
if	((position(':NR_SEQ_TABELA' in upper(ds_sql_w))) > 0) then
	begin
	ds_parametros_sql_w	:= ds_parametros_sql_w || 'nr_seq_tabela=' || nr_seq_tabela_p || ';';
	end;
end if;

qt_real_w := obter_valor_dinamico_bv(ds_sql_w, ds_parametros_sql_w, qt_real_w);
qt_indicador_p	:= coalesce(qt_real_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
