SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cus_gerar_comp_ficha_kit ( cd_estabelecimento_p bigint, nr_ficha_tecnica_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_material_w			bigint;
cd_kit_material_w			bigint;
cd_material_comp_w		bigint;
nr_seq_componente_w		bigint;
qt_componente_w			double precision;

C01 CURSOR FOR
SELECT	cd_material,
	qt_material
from	componente_kit
where	cd_kit_material	= cd_kit_material_w
and	ie_situacao	= 'A'
and	((coalesce(cd_estab_regra::text, '') = '') or (cd_estab_regra = cd_estabelecimento_p));


BEGIN

select	cd_material
into STRICT	cd_material_w
from	ficha_tecnica
where	nr_ficha_tecnica	= nr_ficha_tecnica_p;

select	coalesce(max(cd_kit_material),0)
into STRICT	cd_kit_material_w
from	material_estab
where	cd_estabelecimento	= cd_estabelecimento_p
and	cd_material	= cd_material_w;

delete	FROM ficha_tecnica_componente
where	nr_ficha_tecnica	 = nr_ficha_tecnica_p
and	ie_tipo_componente = 1;

select	coalesce(max(nr_seq_componente),0)
into STRICT	nr_seq_componente_w
from	ficha_tecnica_componente
where	nr_ficha_tecnica	= nr_ficha_tecnica_p;

open C01;
loop
fetch C01 into
	cd_material_comp_w,
	qt_componente_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	nr_seq_componente_w	:= nr_seq_componente_w + 1;

	insert into ficha_tecnica_componente(
		nr_ficha_tecnica,
		nr_seq_componente,
		dt_alteracao,
		cd_estabelecimento,
		nm_usuario,
		dt_atualizacao,
		ie_situacao,
		ie_tipo_componente,
		cd_material,
		qt_variavel,
		qt_fixa,
		pr_quebra_variavel,
		dt_atualizacao_nrec,
		nm_usuario_nrec)
	values (	nr_ficha_tecnica_p,
		nr_seq_componente_w,
		clock_timestamp(),
		cd_estabelecimento_p,
		nm_usuario_p,
		clock_timestamp(),
		'A',
		1,
		cd_material_comp_w,
		qt_componente_w,
		0,
		0,
		clock_timestamp(),
		nm_usuario_p);

	end;
end loop;
close C01;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
