CREATE OR REPLACE PROCEDURE cus_gerar_parametro_tabela ( cd_estabelecimento_p bigint, cd_tipo_tabela_custo_p bigint, cd_tabela_custo_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_parametro_w	integer;
qt_registro_w		bigint;

C01 CURSOR FOR
SELECT	b.nr_seq_parametro
from	tipo_tabela_custo_param b
where	b.cd_tipo_tabela_custo	= cd_tipo_tabela_custo_p;


BEGIN

open C01;
loop

fetch C01 into
	nr_seq_parametro_w;
EXIT WHEN NOT FOUND; /* apply on C01 */

	select	count(*)
	into STRICT	qt_registro_w
	from	tabela_parametro
	where	cd_estabelecimento		= cd_estabelecimento_p
	and	cd_tabela_custo		= cd_tabela_custo_p
	and	nr_seq_parametro		= nr_seq_parametro_w;

	if (qt_registro_w = 0) then

		insert into tabela_parametro(
			cd_estabelecimento,
			cd_tabela_custo,
			nr_seq_parametro,
			nm_usuario,
			dt_atualizacao,
			cd_tabela_parametro,
			cd_comp_parametro,
			dt_parametro,
			qt_parametro)
		values (	cd_estabelecimento_p,
			cd_tabela_custo_p,
			nr_seq_parametro_w,
			nm_usuario_p,
			clock_timestamp(),
			null,
			null,
			null,
			null);

	end if;
end loop;
close C01;

commit;
end;
