CREATE OR REPLACE PROCEDURE cus_obter_indicador_criterio ( cd_estabelecimento_p bigint, nr_seq_tabela_p bigint, cd_centro_controle_p bigint, nr_seq_indicador_p bigint, cd_sequencia_criterio_p INOUT bigint, qt_distribuicao_p INOUT bigint) AS $body$
DECLARE


cd_sequencia_criterio_w		bigint;
qt_distribuicao_w			double precision;


BEGIN

select	max(cd_sequencia_criterio)
into STRICT	cd_sequencia_criterio_w
from	resultado_ind_criterio
where	cd_estabelecimento	= cd_estabelecimento_p
and	nr_seq_tabela	= nr_seq_tabela_p
and	nr_seq_indicador	= nr_seq_indicador_p;

if (cd_sequencia_criterio_w IS NOT NULL AND cd_sequencia_criterio_w::text <> '') then

	select	coalesce(max(a.qt_distribuicao),0)
	into STRICT	qt_distribuicao_w
	from	criterio_distr_orc_dest a
	where	a.cd_sequencia_criterio	= cd_sequencia_criterio_w
	and	a.cd_centro_controle_dest	= cd_centro_controle_p;
end if;

qt_distribuicao_p		:= coalesce(qt_distribuicao_w,0);
cd_sequencia_criterio_p	:= cd_sequencia_criterio_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
