CREATE OR REPLACE PROCEDURE cus_obter_sit_nat_gasto (nr_seq_ng_p bigint, nr_seq_ng_dest_p bigint, nr_seq_ng_origem_p bigint, ie_situacao_p INOUT text, ie_tipo_p INOUT text) AS $body$
DECLARE


ie_tipo_w		varchar(5) := '';
ie_situacao_w		varchar(1) := '';


BEGIN
if (nr_seq_ng_p IS NOT NULL AND nr_seq_ng_p::text <> '') then
	begin
	select	coalesce(a.ie_situacao, 'I')
	into STRICT	ie_situacao_w
	from	natureza_gasto a
	where	a.nr_sequencia	= nr_seq_ng_p;
		ie_tipo_w := 'NG';
	end;
end if;

if (ie_situacao_w <> 'I') and (nr_seq_ng_dest_p IS NOT NULL AND nr_seq_ng_dest_p::text <> '') then
	begin
	select	coalesce(a.ie_situacao, 'I')
	into STRICT	ie_situacao_w
	from	natureza_gasto a
	where	a.nr_sequencia	= nr_seq_ng_dest_p;
	ie_tipo_w := 'DEST';
	end;
end if;

if (ie_situacao_w <> 'I') and (nr_seq_ng_origem_p IS NOT NULL AND nr_seq_ng_origem_p::text <> '') then
	begin
	select	coalesce(a.ie_situacao, 'I')
	into STRICT	ie_situacao_w
	from	natureza_gasto a
	where	a.nr_sequencia	= nr_seq_ng_origem_p;
	ie_tipo_w := 'ORG';
	end;
end if;

ie_situacao_p	:= ie_situacao_w;
ie_tipo_p	:= ie_tipo_w;

end;
