select	rpad('R18A',4,' ')
	||lpad((to_char(dt_referencia, 'MM'))::numeric ,2,0)
	||lpad(3,2,0) --coluna,
	||lpad(replace(campo_mascara(coalesce(round(((obter_dados_dacon(nr_seq_dacon_p, 'AC') * obter_dados_dacon(nr_seq_dacon_p, 'TBCC'))/100),2),'0'),2),'.',''),14,0)
 	||lpad(0,14,0)
