SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE dar_altera_publicacao_app (nr_sequencia_p dar_app.nr_sequencia%type) AS $body$
DECLARE


   dt_publicacao_w dar_app.dt_publicacao%type;


BEGIN

   -- Se ja publicado, passa a ser null a data de publicacao 
   select max(CASE WHEN coalesce(a.dt_publicacao::text, '') = '' THEN  clock_timestamp()  ELSE null END )
     into STRICT dt_publicacao_w
     from dar_app a
    where a.nr_sequencia = nr_sequencia_p;

   update dar_app
      set dt_publicacao = dt_publicacao_w
    where nr_sequencia = nr_sequencia_p;

   commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
