CREATE OR REPLACE PROCEDURE dar_atlz_dados_table_control (nr_seq_app_p dar_app.nr_sequencia%type) AS $body$
DECLARE

   c1 CURSOR FOR
      SELECT distinct(c.nr_sequencia)
        from dar_app a, dar_app_datamodels b, dar_tables_control c
       where a.nr_sequencia = b.nr_seq_app
         and coalesce(b.nr_seq_ligacao, b.nr_seq_table_control) = c.nr_sequencia
         and ((a.dt_publicacao IS NOT NULL AND a.dt_publicacao::text <> '') or a.nr_sequencia = coalesce(nr_seq_app_p,a.nr_sequencia));
   r1 c1%rowtype;

BEGIN
   open c1;
   loop
      fetch c1
         into r1;
      EXIT WHEN NOT FOUND; /* apply on c1 */
      --
      CALL dar_gerar_tab_data_model(nr_sequencia_p => r1.nr_sequencia,
                               nm_usuario_p   => wheb_usuario_pck.get_nm_usuario);

   end loop;
   close c1;
   commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
