CREATE OR REPLACE PROCEDURE dashboard_gerar_proj_graf (nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w  bigint;
ds_projeto_w  varchar(255);
pr_prev_w   double precision;
pr_real_w   double precision;
qt_hora_prev_w  double precision;
qt_hora_real_w  double precision;

C01 CURSOR FOR
  SELECT   a.nr_sequencia,
           a.ds_titulo
  from     dashboard_pmo_proj_resumo_v a
  where    (a.nr_seq_programa IS NOT NULL AND a.nr_seq_programa::text <> '')
  and      coalesce(a.dt_fim_real::text, '') = ''
  and      a.pr_real < 100
  order by a.ds_titulo;


BEGIN

open C01;
loop
fetch C01 into
  nr_sequencia_w,
  ds_projeto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
  begin

  select  max(a.pr_prev),
      max(a.pr_real),
      max(a.qt_hora_real),
      max(a.qt_hora_prev)
  into STRICT  pr_prev_w,
      pr_real_w,
      qt_hora_real_w,
      qt_hora_prev_w
  from  dashboard_pmo_proj_resumo_v a
  where a.nr_sequencia = nr_sequencia_w;

  insert into dashboard_pmo_proj_graf(nr_sequencia,
                    dt_atualizacao_nrec,
                    dt_atualizacao,
                    nm_usuario_nrec,
                    nm_usuario,
                    nr_seq_proj,
                    pr_prev,
                    pr_real,
                    ds_projeto,
                    dt_referencia,
                    qt_hora_prev,
                    qt_hora_real)
            values (    nextval('dashboard_pmo_proj_graf_seq'),
                    clock_timestamp(),
                    clock_timestamp(),
                    nm_usuario_p,
                    nm_usuario_p,
                    nr_sequencia_w,
                    pr_prev_w,
                    pr_real_w,
                    ds_projeto_w,
                    clock_timestamp(),
                    qt_hora_prev_w,
                    qt_hora_real_w);

  end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
