CREATE OR REPLACE PROCEDURE db_add_macro_template_item ( nr_seq_macro_p db_content_macro.nr_sequencia%type, nr_seq_template_p db_content_template.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/*
	++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
	Objective: Link a macro to the Template.
	------------------------------------------------------------------------
	Direct call: 
	[   ] Dictionary objects
	[ X ] Tasy (Delphi/Java/HTML5)
	[   ] Portal
	[   ] Reports
	[   ] Others:
	 -----------------------------------------------------------------------
	Attention points: N/A
	------------------------------------------------------------------------
	References:
		Tables:	db_content_macro,
			db_content_template_item
		Objects: N/A
	------------------------------------------------------------------------
	Called in:
		Java: N/A
		HTML5: Schematics: 1156643
		Object: N/A
	++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
*/
nr_seq_macro_w		db_content_macro.nr_sequencia%type;
nr_seq_template_w	db_content_template.nr_sequencia%type;

ie_already_inserted_w	varchar(1);


BEGIN

	select	cm.nr_sequencia
	into STRICT	nr_seq_macro_w
	from	db_content_macro cm
	where	cm.nr_sequencia = nr_seq_macro_p;
	
	select	ct.nr_sequencia
	into STRICT	nr_seq_template_w
	from	db_content_template ct
	where	ct.nr_sequencia = nr_seq_template_p;
	
	
	if (nr_seq_macro_w IS NOT NULL AND nr_seq_macro_w::text <> '' AND nr_seq_template_w IS NOT NULL AND nr_seq_template_w::text <> '') then
		begin
		
			select	coalesce(max('S'), 'N')
			into STRICT	ie_already_inserted_w
			from	db_content_template_item cti
			where	cti.nr_seq_template = nr_seq_template_w
			and	cti.nr_seq_content_macro = nr_seq_macro_w;
		
			if (ie_already_inserted_w = 'N') then
				begin
					insert
					into db_content_template_item(
						nr_sequencia,
						nr_seq_template,
						nr_seq_content_macro,
						nm_usuario,
						nm_usuario_nrec,
						dt_atualizacao,
						dt_atualizacao_nrec
					)
					values (
						nextval('db_content_template_item_seq'),
						nr_seq_template_w,
						nr_seq_macro_w,
						nm_usuario_p,
						nm_usuario_p,
						clock_timestamp(),
						clock_timestamp()
					);
					commit;
				end;
			end if;
		end;
	end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
