CREATE OR REPLACE PROCEDURE db_document_generate_rev_hist ( nr_seq_documento_p bigint, ds_historico_p text, dt_liberacao_p timestamp, nm_usuario_resp_p text, cd_revisao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_w	bigint;
nr_seq_doc_origem_w	db_document.nr_sequencia%type;


BEGIN
	select	nextval('db_document_hist_seq')
	into STRICT	nr_seq_w
	;
	
	select	max(coalesce(d.nr_seq_source, nr_seq_documento_p))
	into STRICT	nr_seq_doc_origem_w
	from	db_document d
	where	d.nr_sequencia = nr_seq_documento_p;
	
	insert
	into db_document_hist(
			nr_sequencia,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			dt_atualizacao,
			nm_usuario,
			nr_seq_doc_origem,
			ds_historico,
			dt_liberacao,
			nr_seq_documento,
			nm_usuario_resp,
			cd_revisao
		)
		values (
			nr_seq_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_doc_origem_w,
			ds_historico_p,
			dt_liberacao_p,
			nr_seq_documento_p,
			nm_usuario_resp_p,
			cd_revisao_p
		);
	commit;
	
end;
$body$
