CREATE OR REPLACE PROCEDURE db_document_generate ( nm_usuario_p text, nr_seq_doc_template_p bigint, ds_title_p text, cd_document_id_p text, cd_revision_p text, ds_content_p bytea, nr_seq_source_p bigint default null, nr_seq_program_p text DEFAULT NULL, nr_seq_document_p INOUT bigint DEFAULT NULL) AS $body$
DECLARE


nr_seq_w	bigint;
nr_seq_source_w	db_document.nr_sequencia%type;


BEGIN
	select	nextval('db_document_seq')
	into STRICT	nr_seq_w
	;
	
	select	max(coalesce(d.nr_seq_source, nr_seq_source_p))
	into STRICT	nr_seq_source_w
	from	db_document d
	where	d.nr_sequencia = nr_seq_source_p;
	
	insert
	into db_document(
			nr_sequencia,
