			nm_usuario_nrec,
			dt_atualizacao,
			nm_usuario,
			ds_title,
			cd_document_id,
			cd_revision,
			nr_seq_document_template,
			ds_source_content,
			ds_content,
			nr_seq_source,
			nr_seq_program
		)
		values (
			nr_seq_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			ds_title_p,
			cd_document_id_p,
			cd_revision_p,
			nr_seq_doc_template_p,
			ds_content_p,
			ds_content_p,
			nr_seq_source_w,
			nr_seq_program_p
		);
	commit;
	
	nr_seq_document_p := nr_seq_w;
	
