CREATE OR REPLACE PROCEDURE db_document_regenerate ( nm_usuario_p text, nr_seq_doc_template_p bigint, ds_title_p text, cd_document_id_p text, cd_revision_p text, ds_content_p bytea, nr_seq_source_p bigint default null, nr_seq_documento_p bigint DEFAULT NULL) AS $body$
DECLARE


nr_seq_source_w	db_document.nr_sequencia%type;


BEGIN

	select	max(coalesce(d.nr_seq_source, nr_seq_source_p))
	into STRICT	nr_seq_source_w
	from	db_document d
	where	d.nr_sequencia = nr_seq_source_p;
	
	update 	db_document
	set 	dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		ds_title = ds_title_p,
		cd_document_id = cd_document_id_p,
		cd_revision = cd_revision_p,
		nr_seq_document_template = nr_seq_doc_template_p,
		ds_source_content = ds_content_p,
		ds_content = ds_content_p,
		nr_seq_source = nr_seq_source_w
	where 	nr_sequencia = nr_seq_documento_p;
	
	commit;
	
end;
$body$
