SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE db_document_template_version ( nr_seq_document_template_p bigint, nm_usuario_p text, nr_seq_new_version_p INOUT bigint) AS $body$
DECLARE


nr_seq_source_w		db_document_template.nr_sequencia%type;
nr_seq_template_w	db_document_template.nr_sequencia%type;
nr_version_w		db_document_template.nr_version%type;


BEGIN

	select	coalesce(nr_seq_source, nr_seq_document_template_p)
	into STRICT	nr_seq_source_w
	from	db_document_template a
	where	nr_sequencia = nr_seq_document_template_p;
	
	--children record
	if (nr_seq_source_w IS NOT NULL AND nr_seq_source_w::text <> '') then
		select	max(nr_version)
		into STRICT	nr_version_w
		from	db_document_template a
		where	coalesce(nr_seq_source, nr_sequencia) = nr_seq_source_w;
	
		select	nextval('db_document_template_seq')
		into STRICT	nr_seq_template_w
		;
	
		insert into db_document_template(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ds_template,
			cd_revision,
			nr_version,
			ds_content,
			nr_seq_content_builder,
			nr_seq_source)
		SELECT	nr_seq_template_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			ds_template,
			cd_revision,
			nr_version_w + 1,
			ds_content,
			nr_seq_content_builder,
			nr_seq_source_w
		from	db_document_template a
		where	nr_sequencia = nr_seq_source_w;
	
		insert
		into db_document_template_macro(
				ds_variable,
				dt_atualizacao,
				dt_atualizacao_nrec,
				nm_usuario,
				nm_usuario_nrec,
				nr_seq_content_macro,
				nr_seq_doc_template,
				nr_sequencia,
				nr_seq_var_version
			)
		SELECT	dtc.ds_variable,
			dtc.dt_atualizacao,
			dtc.dt_atualizacao_nrec,
			dtc.nm_usuario,
			dtc.nm_usuario_nrec,
			dtc.nr_seq_content_macro,
			nr_seq_template_w,
			nextval('db_document_template_macro_seq'),
			dtc.nr_seq_var_version
		from	db_document_template_macro dtc
		where	dtc.nr_seq_doc_template = nr_seq_source_w;
	
		commit;
	end if;
	
	nr_seq_new_version_p := nr_seq_template_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
